import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, Link, useNavigate } from "react-router-dom";
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

const AttendanceDetails = () => {
  const [employee, setEmployee] = useState(null);
  const [attendance, setAttendance] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [employeeId, setEmployeeId] = useState('');  // Employee ID state
  const [date, setDate] = useState(new Date());
  const navigate = useNavigate();  // For navigation if needed

  // Function to handle fetching employee attendance based on the entered employeeId
  const fetchEmployeeAttendance = async (id) => {
    setLoading(true);
    setError(null);  // Reset the error
    try {
      const response = await axios.get(`http://localhost:3005/employees/${id}/attendance-list`);
      setEmployee(response.data);
      setAttendance(response.data.attendanceRecords || []);
    } catch (err) {
      setError("Error fetching employee data. Please try again later.");
      console.error("Error fetching employee data:", err);
    } finally {
      setLoading(false);
    }
  };

  // Generate days for the current selected month
  const generateMonthDays = (year, month) => {
    const daysInMonth = new Date(year, month + 1, 0).getDate();
    const days = [];
    for (let day = 1; day <= daysInMonth; day++) {
      days.push(new Date(year, month, day));
    }
    return days;
  };

  const fullMonthAttendance = generateMonthDays(date.getFullYear(), date.getMonth()).map(day => {
    const record = attendance.find(att => new Date(att.location.time).toDateString() === day.toDateString());
    let status = null;
    if (record) {
      if (record.AttendanceStatus.isPresent) {
        status = "Present";
      } else if (record.AttendanceStatus.isHalfDay) {
        status = "Half Day";
      } else if (record.AttendanceStatus.isLeave) {
        status = "Leave";
      } else {
        status = "Absent";
      }
    }
    return {
      date: day,
      status,
    };
  });

  const handleSearch = (e) => {
    e.preventDefault();
    if (employeeId.trim()) {
      fetchEmployeeAttendance(employeeId);  // Fetch attendance when search is submitted
    }
  };

  return (
    <div className="p-6 max-w-7xl mx-auto bg-gray-100 rounded-lg shadow-lg absolute top-20 right-10 left-[275px]">
      <h2 className="text-4xl font-bold mb-6 text-center text-blue-600">Employee Attendance</h2>
      
      {/* Search Bar */}
      <form onSubmit={handleSearch} className="mb-4 text-center">
        <input 
          type="text" 
          placeholder="Enter Employee ID" 
          value={employeeId}
          onChange={(e) => setEmployeeId(e.target.value)}
          className="border border-gray-300 rounded-md px-4 py-2 w-64 mr-2"
        />
        <button type="submit" className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
          Search
        </button>
      </form>

      <div className="mb-6">
        <Calendar 
          onChange={setDate} 
          value={date} 
          className="mx-auto" 
        />
        <p className="text-center text-gray-600 mt-2">Selected Month: {date.toLocaleString('default', { month: 'long', year: 'numeric' })}</p>
      </div>

      {loading ? (
        <p className="text-center text-gray-500">Loading attendance...</p>
      ) : error ? (
        <p className="text-center text-red-500">{error}</p>
      ) : employee ? (
        <div className="mb-8">
          <div className="mt-4 mb-4 text-right">
            <Link to='./leaves'>
              <button className="bg-red-500 mr-4 hover:bg-red-700 text-white font-bold py-2 px-4 rounded shadow-lg">Leave Requests</button>
            </Link>
            <Link to='./complaints'>
              <button className="bg-yellow-500 hover:bg-yellow-700 text-white font-bold py-2 px-4 rounded">
                Complaints
              </button>
            </Link>
          </div>
          
          <div className="bg-blue-500 text-white p-4 rounded-lg">
            <h3 className="text-2xl font-semibold">Employee Details</h3>
            <p><strong>ID:</strong> {employee.employeeId}</p>
            <p><strong>Name:</strong> {employee.name}</p>
            <p><strong>Mobile Number:</strong> {employee.mobileNumber}</p>
            <p><strong>Selected Month:</strong> {date.toLocaleString('default', { month: 'long', year: 'numeric' })}</p>
          </div>

          <table className="min-w-full bg-white mt-4 border border-gray-300 rounded-lg shadow-md">
            <thead className="bg-gray-200">
              <tr>
                <th className="border border-gray-300 px-4 py-2 text-left text-sm font-medium text-gray-600">Date</th>
                <th className="border border-gray-300 px-4 py-2 text-left text-sm font-medium text-gray-600">Status</th>
              </tr>
            </thead>
            <tbody>
              {fullMonthAttendance.map(({ date, status }) => (
                <tr key={date.toDateString()} className="hover:bg-gray-100 transition-colors duration-200">
                  <td className="border border-gray-300 px-4 py-2">
                    {date.toDateString()}
                  </td>
                  <td className="border border-gray-300 px-4 py-2">
                    {status ? (
                      <span className={`inline-block px-3 py-1 rounded-full text-sm font-bold ${
                        status === "Present" ? "bg-green-100 text-green-600" :
                        status === "Half Day" ? "bg-yellow-100 text-yellow-600" :
                        status === "Leave" ? "bg-blue-100 text-blue-600" :
                        "bg-red-100 text-red-600"
                      }`}>
                        {status}
                      </span>
                    ) : (
                      <span className="text-gray-500">No record</span>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <p className="text-center text-gray-500">Employee not found.</p>
      )}
    </div>
  );
};

export default AttendanceDetails;
