import { Link, useNavigate } from "react-router-dom";
import React, { useState } from "react";
import {
  FaPhone,
  FaEnvelope,
  FaTachometerAlt,
  FaUsers,
  FaClipboardList, // Import icon for Attendance
  FaMapMarkerAlt, // Import icon for Location
} from "react-icons/fa";

const Sidebar = () => {
  const [showSearchBar, setShowSearchBar] = useState(false); // State to toggle search bar
  const [employeeId, setEmployeeId] = useState(""); // State for employee ID input
  const navigate = useNavigate();

  // Handle showing search bar when "Call Log" is selected
  const handleCallLogClick = () => {
    setShowSearchBar(true);
  };

  // Handle form submission for searching call logs by employee ID
  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (employeeId.trim() !== "") {
      navigate(`/calllogs/${employeeId}`); // Navigate to call logs page for specific employee
      setEmployeeId(""); // Reset the input
      setShowSearchBar(false); // Hide search bar after submission
    }
  };

  return (
    <aside className="w-64 fixed bottom-0 top-16 bg-gray-800 text-white h-full p-4">
      <nav className="space-y-4">
        {/* Dashboard Link */}
        <Link
          to="/"
          className="flex text-[20px] items-center space-x-2 p-2 hover:bg-gray-700 rounded"
        >
          <FaTachometerAlt className="text-yellow-400" />
          <span>Dashboard</span>
        </Link>

        {/* Employee List Link */}
        <Link
          to="/employees"
          className="text-[20px] flex items-center space-x-2 p-2 hover:bg-gray-700 rounded"
        >
          <FaUsers className="text-purple-400" />
          <span>Employee List</span>
        </Link>

        {/* Call Log Link */}
        <div className="flex flex-col">
          <button
            onClick={handleCallLogClick}
            className="flex text-[20px] items-center space-x-2 p-2 hover:bg-gray-700 rounded"
          >
            <FaPhone className="text-blue-400" />
            <span>Call Log</span>
          </button>

          {/* Conditionally render search bar only when "Call Log" is selected */}
          {showSearchBar && (
            <form onSubmit={handleSearchSubmit} className="mt-2">
              <input
                type="text"
                value={employeeId}
                onChange={(e) => setEmployeeId(e.target.value)}
                placeholder="Enter Employee ID"
                className="w-full p-2 rounded bg-gray-700 text-white"
              />
              <button
                type="submit"
                className="w-full bg-blue-500 p-2 mt-2 rounded text-white"
              >
                Search
              </button>
            </form>
          )}
        </div>

        {/* Messages Link */}
        <Link
          to="/message-details"
          className="flex text-[20px] items-center space-x-2 p-2 hover:bg-gray-700 rounded"
        >
          <FaEnvelope className="text-green-400" />
          <span>Messages</span>
        </Link>

        {/* Location Link */}
        <Link
          to="/location"
          className="text-[20px] flex items-center space-x-2 p-2 hover:bg-gray-700 rounded"
        >
          <FaMapMarkerAlt className="text-red-400" />
          <span>Location</span>
        </Link>

        {/* Attendance Link */}
        <Link
          to="/attendance-Details"
          className="text-[20px] flex items-center space-x-2 p-2 hover:bg-gray-700 rounded"
        >
          <FaClipboardList className="text-orange-400" />
          <span>Attendance</span>
        </Link>
      </nav>
    </aside>
  );
};

export default Sidebar;
