import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, Link } from "react-router-dom";
import Calendar from 'react-calendar';  // Import the Calendar component
import 'react-calendar/dist/Calendar.css';  // Import the CSS for the calendar

const EmployeeAttendance = () => {
  const [employee, setEmployee] = useState(null);  // Single employee
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [inputEmployeeId, setInputEmployeeId] = useState("");  // State for entered employee ID
  const [attendanceData, setAttendanceData] = useState([]);  // Attendance data
  const [date, setDate] = useState(new Date());  // State for selected date
  const [daysInMonth, setDaysInMonth] = useState([]);  // Days in selected month
  const [errorMessage, setErrorMessage] = useState(null);

  // Handle employee ID input change
  const handleEmployeeIdChange = (e) => {
    setInputEmployeeId(e.target.value);
  };

  // Handle form submission to fetch attendance data
  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent page reload
    if (!inputEmployeeId) {
      setErrorMessage("Please enter a valid Employee ID.");
      return;
    }
    setErrorMessage(null);
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:3005/employees/${inputEmployeeId}/attendance-list`);
      setEmployee(response.data.employee);
      setAttendanceData(response.data.attendance); // Assuming attendance data is an array
    } catch (err) {
      setErrorMessage("Error fetching employee attendance. Please try again.");
      console.error("Error fetching employee data:", err);
    } finally {
      setLoading(false);
    }
  };

  // Update the days for the selected month
  useEffect(() => {
    const getDaysInSelectedMonth = (year, month) => {
      return new Date(year, month + 1, 0).getDate();  // Get the last date of the month
    };

    const month = date.getMonth();
    const year = date.getFullYear();
    const days = getDaysInSelectedMonth(year, month);
    setDaysInMonth(Array.from({ length: days }, (_, i) => i + 1));  // Create an array of days for the month
  }, [date]);

  return (
    <div className="p-6 max-w-7xl mx-auto bg-gray-100 rounded-lg shadow-lg absolute top-20 right-10 left-[275px]">
      <h2 className="text-4xl font-bold mb-6 text-center text-blue-600">Employee Attendance</h2>

      {/* Form to enter Employee ID */}
      <form onSubmit={handleSubmit} className="mb-4 mt-6">
        <input
          type="text"
          placeholder="Enter Employee ID"
          value={inputEmployeeId}
          onChange={handleEmployeeIdChange}
          className="border p-2 rounded mr-2 border border-purple-500 focus:border-purple-700 focus:ring-2 focus:ring-purple-500"
        />
        <button
          type="submit"
          className="bg-gradient-to-r from-blue-500 to-blue-700 hover:shadow-lg text-white px-6 py-2 rounded-lg"
        >
          Fetch Attendance
        </button>
      </form>

      {errorMessage && <p className="text-red-500">{errorMessage}</p>}

      {/* Calendar Component */}
      <div className="mb-6">
        <Calendar 
          onChange={setDate} 
          value={date} 
          className="mx-auto" 
        />
        <p className="text-center text-gray-600 mt-2">Selected Date: {date.toDateString()}</p>
      </div>

      {loading ? (
        <p className="text-center text-gray-500">Loading attendance...</p>
      ) : error ? (
        <p className="text-center text-red-500">{error}</p>
      ) : employee ? (
        <div className="mb-8">
          {/* Buttons to navigate to leave requests and complaints */}
          <div className="mt-4 mb-4 text-right">
            <Link to='./leaves'>
              <button className="bg-red-500 mr-4 hover:bg-red-700 text-white font-bold py-2 px-4 rounded shadow-lg">Leave Requests</button>
            </Link>
            <Link to='./complaints'>
              <button className="bg-yellow-500 hover:bg-yellow-700 text-white font-bold py-2 px-4 rounded">
                Complaints
              </button>
            </Link>
          </div>

          {/* Employee Details */}
          <div className="bg-blue-500 text-white p-4 rounded-lg">
            <h3 className="text-2xl font-semibold">Employee Details</h3>
            <p><strong>ID:</strong> {employee.employeeId}</p>
            <p><strong>Name:</strong> {employee.name}</p>
            <p><strong>Mobile Number:</strong> {employee.mobileNumber}</p>
            <p><strong>Selected Date:</strong> {date.toDateString()}</p> {/* Display selected date */}
          </div>

          {/* Attendance Table */}
          <table className="min-w-full bg-white mt-4 border border-gray-300 rounded-lg shadow-md">
            <thead className="bg-gray-200">
              <tr>
                <th className="border border-gray-300 px-4 py-2 text-left text-sm font-medium text-gray-600">Date</th>
                <th className="border border-gray-300 px-4 py-2 text-left text-sm font-medium text-gray-600">Status</th>
                <th className="border border-gray-300 px-4 py-2 text-left text-sm font-medium text-gray-600">Reason for Leave</th>
              </tr>
            </thead>
            <tbody>
              {attendanceData.map((record, index) => (
                <tr key={index} className="hover:bg-gray-100 transition-colors duration-200">
                  <td className="border border-gray-300 px-4 py-2">{record.date}</td>
                  <td className="border border-gray-300 px-4 py-2">
                    <span className={`inline-block px-3 py-1 rounded-full text-sm font-bold ${
                      record.status === 'Present' ? "bg-green-100 text-green-600" : "bg-red-100 text-red-600"
                    }`}>
                      {record.status}
                    </span>
                  </td>
                  <td className="border border-gray-300 px-4 py-2">
                    {record.status !== 'Present' && (
                      <span className="text-red-500">{record.reason}</span>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <p className="text-center text-gray-500">No attendance data found.</p>
      )}
    </div>
  );
};

export default EmployeeAttendance;
