import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

const CallLogTable = () => {
  const { employeeId } = useParams(); // Get employeeId from URL
  const [callLogs, setCallLogs] = useState([]);
  const [allCallLogs, setAllCallLogs] = useState([]); // Store all call logs
  const [error, setError] = useState("");
  const [sortOrder, setSortOrder] = useState(""); // Sort by Duration state
  const [filterType, setFilterType] = useState("All"); // Filter call type state
  const navigate = useNavigate(); // Initialize navigate
  const [filteredCallLogs, setFilteredCallLogs] = useState([]);

  // Fetch call logs whenever employeeId changes
  useEffect(() => {
    if (employeeId) {
      const fetchCallLogs = async () => {
        try {
          const response = await axios.get(
            `http://localhost:3005/employees/${employeeId}/call-logs`
          );
          setCallLogs(response.data);
          setAllCallLogs(response.data); // Store all call logs
        } catch (err) {
          setError("Error fetching call logs");
          console.error("Error fetching call logs:", err.message || err);
        }
      };

      fetchCallLogs();
    } else {
      console.warn("employeeId is undefined. Cannot fetch call logs.");
    }
  }, [employeeId]);

  // Function to handle sorting by duration
  const handleSort = (order) => {
    setSortOrder(order);
    const sortedLogs = [...callLogs].sort((a, b) => {
      if (order === "ascending") {
        return a.duration - b.duration; // Sort ascending
      } else if (order === "descending") {
        return b.duration - a.duration; // Sort descending
      }
      return 0; // No sorting
    });
    setCallLogs(sortedLogs); // Update the state with sorted logs
  };

  // Function to filter call logs by type
  const handleFilter = (type) => {
    setFilterType(type);
    if (type === "All") {
      setCallLogs(allCallLogs);
    } else {
      setCallLogs(allCallLogs.filter((log) => log.type === type));
    }
  };

  const handleDeleteCallLog = async (callLogId) => {
    if (window.confirm("Do you really want to delete this call log?")) {
      try {
        await axios.delete(`http://localhost:3005/Delete-call-log/${employeeId}/${callLogId}`);
        // Update local state to reflect the deleted call log
        setCallLogs((prevCallLogs) => prevCallLogs.filter((log) => log._id !== callLogId));
        setFilteredCallLogs((prevFiltered) => prevFiltered.filter((log) => log._id !== callLogId));
      } catch (error) {
        console.error("Error deleting call log:", error);
      }
    }
  };

  return (
    <div className="p-6 absolute top-20 right-10 left-[270px]">
      <h2 className="text-3xl font-extrabold mb-6 text-gray-800">
        Call Logs for Employee ID: {employeeId}
      </h2>

      <div className="flex justify-between items-center mb-4">
        <input
          type="text"
          placeholder="Search by Name or Phone Number..."
          className="border border-purple-500 focus:border-purple-700 focus:ring-2 focus:ring-purple-500 p-2 rounded-lg w-1/3 shadow-sm"
        />
        <select
          className="border border-purple-500 p-2 rounded-lg focus:outline-none focus:border-purple-700"
          value={filterType}
          onChange={(e) => handleFilter(e.target.value)}
        >
          <option value="All">All</option>
          <option value="incoming">Incoming</option>
          <option value="outgoing">Outgoing</option>
          <option value="missed">Missed</option> {/* New option for Missed calls */}
        </select>
        <button className="bg-gradient-to-r from-red-500 to-purple-700 hover:shadow-lg text-white px-6 py-2 rounded-lg">
          Delete All Call Logs
        </button>
      </div>

      {/* Sort By Duration Dropdown */}
      <div className="flex justify-between items-center mb-4">
        <select
          className="border border-purple-500 p-2 rounded-lg focus:outline-none focus:border-purple-700"
          value={sortOrder}
          onChange={(e) => handleSort(e.target.value)}
        >
          <option value="">Sort by Duration</option>
          <option value="ascending">Ascending</option>
          <option value="descending">Descending</option>
        </select>
      </div>

      {callLogs.length > 0 ? (
        <div className="overflow-x-auto">
          <table className="table-auto w-full text-left border-collapse shadow-lg rounded-lg overflow-hidden">
            <thead>
              <tr className="bg-gradient-to-r from-purple-600 to-purple-400 text-white">
                <th className="p-4">Sl. No.</th>
                <th className="p-4">Name</th>
                <th className="p-4">Type</th>
                <th className="p-4">Duration (seconds)</th>
                <th className="p-4">Date & Time</th>
                <th className="p-4">Phone Number</th>
                <th className="p-4 text-center">Actions</th>
              </tr>
            </thead>
            <tbody className="text-gray-700">
              {callLogs.map((log, index) => (
                <tr
                  key={`${log.timestamp}-${index}`} // Combine timestamp and index for a unique key
                  className="bg-white border-b border-purple-200 hover:bg-purple-50 transition duration-300"
                >
                  <td className="p-4">{index + 1}</td>
                  <td className="p-4">{log.name || "N/A"}</td>
                  <td className="p-4">{log.type}</td>
                  <td className="p-4">{log.duration}</td>
                  <td className="p-4">
                    {new Date(log.dateTime).toLocaleString()}
                  </td>
                  <td className="p-4">{log.phoneNumber}</td>
                  <td className="p-4 text-center">
                    <button
                      onClick={() => handleDeleteCallLog(log._id)}
                      className="bg-gradient-to-r from-red-500 to-purple-700 hover:shadow-lg text-white px-4 py-2 rounded-lg"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <p className="text-center text-lg text-gray-600">
          No call logs available
        </p>
      )}
    </div>
  );
};

export default CallLogTable;
