import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { BASE_URL } from './constants';

const EmployeeDetails = () => {
  const { employeeId } = useParams();
  const [employee, setEmployee] = useState({
    _id: "",
    employeeId: "",
    name: "",
    mobileNumber: "",
    alternateMobileNumber: "",
    branch: "",
    designation: "",
    dateOfBirth: "",
    address: "",
    joinDate: "",
    fatherName: "",
    spouseName: "",
    salary: "",
    gender: "",
    createdAt: "",
    updatedAt: "",
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchEmployeeData();
  }, [employeeId]);

  const fetchEmployeeData = async () => {
    try {
      const response = await axios.get(`${BASE_URL}/employees/${employeeId}/details`);
      setEmployee(response.data);
    } catch (err) {
      setError("Failed to fetch employee data.");
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <form className="bg-gradient-to-r from-blue-400 to-indigo-600 p-8 rounded-lg max-w-lg mx-auto shadow-lg text-white absolute top-20 left-[275px]">
      {Object.keys(employee).map((key) => (
        <label key={key} className="flex flex-col mb-4 font-semibold">
          {key.charAt(0).toUpperCase() + key.slice(1)}
          <input
            type={key.includes('Date') ? 'date' : 'text'}
            value={key.includes('Date') ? (employee[key] ? employee[key].split('T')[0] : '') : employee[key]}
            readOnly
            className="mt-1 p-3 rounded-md bg-white text-gray-800 hover:bg-gray-200 transition duration-200"
          />
        </label>
      ))}
    </form>
  );
};

export default EmployeeDetails;
