import { BrowserRouter as Router, Route, Routes, Navigate, useLocation } from 'react-router-dom';
import Sidebar from './components/Sidebar';
import Dashboard from './components/Dashboard';
import CallLog from './components/CallLog';
import Messages from './components/Messages';
import Location from './components/Location';
import Header from './components/Header';
import Register from './components/Register';

// Private Route component (for protected routes)
const PrivateRoute = ({ children }) => {
  const auth = localStorage.getItem('user'); // Check if user is authenticated
  return auth ? children : <Navigate to="/register" />; // If not authenticated, redirect to register
};

function Layout() {
  const location = useLocation(); // This works inside Router
  return (
    <div className="h-screen flex flex-col">
      {/* Header */}
      <Header />

      <div className="flex flex-1">
        {/* Conditionally render Sidebar only if the current route is not /register */}
        {location.pathname !== '/register' && <Sidebar />}

        {/* Main Content */}
        <main className="flex-1 p-6 bg-gray-100 overflow-y-auto">
          <Routes>
            {/* Public Routes */}
            <Route path="/auth" element={<Auth />} />

            {/* Private Routes */}
            <Route
              path="/"
              element={
                <PrivateRoute>
                  <Dashboard />
                </PrivateRoute>
              }
            />
            <Route
              path="/call-log"
              element={
                <PrivateRoute>
                  <CallLog />
                </PrivateRoute>
              }
            />
            <Route
              path="/messages"
              element={
                <PrivateRoute>
                  <Messages />
                </PrivateRoute>
              }
            />
            <Route
              path="/location"
              element={
                <PrivateRoute>
                  <Location />
                </PrivateRoute>
              }
            />
          </Routes>
        </main>
      </div>
    </div>
  );
}

function App() {
  return (
    <Router>
      <Layout />
    </Router>
  );
}

export default App;
