import React, { useState,useEffect } from 'react';
import axios from 'axios';
const Navigate = useNavigate();

const Register = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [showPopup, setShowPopup] = useState(false);
  const [notification, setNotification] = useState(''); // State for notification message
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    const auth = localStorage.getItem("user");
    if (auth) {
      Navigate("/");
    }
  }, [Navigate]);


  const handleRegisterSuccess = () => {
    setNotification('Registration successful!'); // Set the notification message
    setShowPopup(true);
    setTimeout(() => {
      setShowPopup(false);
      setNotification(''); // Clear notification message
      setIsLogin(true);
    }, 3000);
  };

 

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3005/register', {
        username,
        password,
      });
  
      if (response.status === 201) {
        handleRegisterSuccess();
      } else {
        setErrorMessage('Registration failed');
      }
    } catch (error) {
      // Check if error response exists to show specific error messages
      if (error.response) {
        setErrorMessage(error.response.data.message || 'Succesfully registered');
      } else {
        setErrorMessage('Registration failed'); // General error message
      }
    }
  };
  

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:3005/login', {
        username,
        password,
      });
      // Handle successful login (e.g., redirect to dashboard)
    } catch (error) {
      setErrorMessage(error.response.data.message || 'Login failed');
    }
  };

  return (
    <div className="relative flex items-center justify-center min-h-screen bg-gray-100">
      <div className="w-full max-w-md px-8 py-6 bg-white shadow-lg rounded-lg">
        {/* Tabs */}
        <div className="flex justify-around mb-6 border-b-2 border-gray-200">
          <button
            onClick={() => setIsLogin(true)}
            className={`pb-2 text-xl font-semibold ${
              isLogin ? 'border-b-4 border-blue-500 text-blue-500' : 'text-gray-500'
            }`}
          >
            Login
          </button>
          <button
            onClick={() => setIsLogin(false)}
            className={`pb-2 text-xl font-semibold ${
              !isLogin ? 'border-b-4 border-blue-500 text-blue-500' : 'text-gray-500'
            }`}
          >
            Register
          </button>
        </div>

        {/* Form */}
        {isLogin ? (
          <LoginForm 
            setIsLogin={setIsLogin} 
            handleLogin={handleLogin} 
            setUsername={setUsername} 
            setPassword={setPassword} 
            errorMessage={errorMessage}
          />
        ) : (
          <RegisterForm 
            handleRegister={handleRegister} 
            setUsername={setUsername} 
            setPassword={setPassword} 
            errorMessage={errorMessage}
          />
        )}
      </div>

      {/* Notification Popup */}
      {showPopup && (
        <div className="absolute top-10 left-1/2 transform -translate-x-1/2 p-4 text-white bg-green-500 rounded-lg shadow-lg">
          {notification}
        </div>
      )}
    </div>
  );
};

// Login Form Component
const LoginForm = ({ setIsLogin, handleLogin, setUsername, setPassword, errorMessage }) => (
  <form onSubmit={handleLogin}>
    <div className="mb-4">
      <label className="block text-gray-700">User Name</label>
      <input
        type="text"
        placeholder="Enter User Name"
        className="w-full px-3 py-2 mt-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
        required
        onChange={(e) => setUsername(e.target.value)}
      />
    </div>
    <div className="mb-4">
      <label className="block text-gray-700">Password</label>
      <input
        type="password"
        placeholder="Enter Password"
        className="w-full px-3 py-2 mt-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
        required
        onChange={(e) => setPassword(e.target.value)}
      />
    </div>
    {errorMessage && <div className="mb-4 text-red-500">{errorMessage}</div>}
    <button type="submit" className="w-full py-2 mt-4 text-white bg-blue-500 rounded-lg hover:bg-blue-600">
      Login
    </button>

    <div className="mt-4 text-center">
      <span className="text-gray-600">Don't have an account? </span>
      <button
        type="button"
        onClick={() => setIsLogin(false)}
        className="font-semibold text-blue-500 hover:underline"
      >
        Register
      </button>
    </div>
  </form>
);

// Register Form Component
const RegisterForm = ({ handleRegister, setUsername, setPassword, errorMessage }) => (
  <form onSubmit={handleRegister}>
    <div className="mb-4">
      <label className="block text-gray-700">User Name</label>
      <input
        type="text"
        placeholder="Enter User Name"
        className="w-full px-3 py-2 mt-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
        required
        onChange={(e) => setUsername(e.target.value)}
      />
    </div>
    <div className="mb-4">
      <label className="block text-gray-700">Password</label>
      <input
        type="password"
        placeholder="Enter Password"
        className="w-full px-3 py-2 mt-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
        required
        onChange={(e) => setPassword(e.target.value)}
      />
    </div>
    {errorMessage && <div className="mb-4 text-red-500">{errorMessage}</div>}
    <button type="submit" className="w-full py-2 mt-4 text-white bg-blue-500 rounded-lg hover:bg-blue-600">
      Register
    </button>
  </form>
);

export default Register;
