import React, { useEffect, useState } from "react";
import axios from "axios";
import { FaPhone, FaEnvelope, FaMapMarkerAlt } from "react-icons/fa";
import { Link, useParams } from "react-router-dom";
import Sidebar from "./Sidebar";

// Reusable table for displaying recent calls
const RecentActivityTable = ({ recentCalls }) => {
  return (
    <div className="mt-8 bg-gray-200 shadow-lg shadow-gray-500 rounded-lg p-6">
      <h2 className="text-xl font-bold mb-4">Recent Call Logs</h2>
      <table className="min-w-full table-auto">
        <thead>
          <tr>
            <th className="px-4 py-2 text-left text-gray-600">Caller</th>
            <th className="px-4 py-2 text-left text-gray-600">Time</th>
            <th className="px-4 py-2 text-left text-gray-600">Duration</th>
            <th className="px-4 py-2 text-left text-gray-600">Status</th>
          </tr>
        </thead>
        <tbody>
          {recentCalls.map((call, index) => (
            <tr key={index} className="hover:bg-blue-200">
              <td className="border px-4 py-2">{call.name || "N/A"}</td>
              <td className="border px-4 py-2">
                {new Date(call.dateTime).toLocaleTimeString()}
              </td>
              <td className="border px-4 py-2">{call.duration}</td>
              <td
                className={`border px-4 py-2 ${
                  call.status === "Completed" ? "text-green-500" : "text-red-500"
                }`}
              >
                {call.status}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

// Call log table with search, filter, sort, and delete functionalities
const CallLogTable = () => {
  const { employeeId } = useParams();
  const [callLogs, setCallLogs] = useState([]);
  const [filteredCallLogs, setFilteredCallLogs] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [sortOrder, setSortOrder] = useState("");
  const [filterType, setFilterType] = useState("All");

  useEffect(() => {
    if (employeeId) {
      const fetchCallLogs = async () => {
        try {
          const response = await axios.get(
            `http://localhost:3005/employees/${employeeId}/call-logs`
          );
          setCallLogs(response.data);
          setFilteredCallLogs(response.data);
        } catch (error) {
          console.error("Error fetching call logs:", error);
        }
      };

      fetchCallLogs();
    }
  }, [employeeId]);

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSort = (order) => {
    setSortOrder(order);
    const sortedLogs = [...filteredCallLogs].sort((a, b) => {
      return order === "ascending"
        ? a.duration - b.duration
        : b.duration - a.duration;
    });
    setFilteredCallLogs(sortedLogs);
  };

  const handleDeleteCallLog = async (callLogId) => {
    if (window.confirm("Do you really want to delete this call log?")) {
      try {
        await axios.delete(
          `http://localhost:3005/Delete-call-log/${employeeId}/${callLogId}`
        );
        setCallLogs((prevLogs) =>
          prevLogs.filter((log) => log._id !== callLogId)
        );
        setFilteredCallLogs((prevFiltered) =>
          prevFiltered.filter((log) => log._id !== callLogId)
        );
      } catch (error) {
        console.error("Error deleting call log:", error);
      }
    }
  };

  useEffect(() => {
    const filtered = callLogs.filter((log) => {
      const name = log.name || "";
      const phoneNumber = log.phoneNumber || "";
      const type = log.type || "";
      const date = new Date(log.dateTime).toLocaleDateString();

      return (
        (name.toLowerCase().includes(searchTerm.toLowerCase()) ||
          phoneNumber.includes(searchTerm) ||
          log.duration.toString().includes(searchTerm) ||
          type.toLowerCase().includes(searchTerm.toLowerCase()) ||
          date.includes(searchTerm)) &&
        (filterType === "All" || log.type === filterType)
      );
    });
    setFilteredCallLogs(filtered);
  }, [searchTerm, callLogs, filterType]);

  return (
    <div className="p-6 absolute top-20 right-10 left-[270px]">
      <h2 className="text-3xl font-extrabold mb-6 text-gray-800">
        Call Logs for Employee ID: {employeeId}
      </h2>

      <div className="flex justify-between items-center mb-4">
        <input
          type="text"
          placeholder="Search by Name or Phone Number..."
          value={searchTerm}
          onChange={handleSearchChange}
          className="border p-2 rounded-lg w-1/3"
        />
        <select
          className="border p-2 rounded-lg"
          value={sortOrder}
          onChange={(e) => handleSort(e.target.value)}
        >
          <option value="">Sort by Duration</option>
          <option value="ascending">Ascending</option>
          <option value="descending">Descending</option>
        </select>
        <button
          onClick={() => setFilteredCallLogs([])}
          className="bg-gradient-to-r from-red-500 to-purple-700 text-white px-6 py-2 rounded-lg"
        >
          Delete All Call Logs
        </button>
      </div>

      {filteredCallLogs.length > 0 ? (
        <div className="overflow-x-auto">
          <table className="table-auto w-full text-left border-collapse shadow-lg rounded-lg">
            <thead>
              <tr className="bg-gradient-to-r from-purple-600 to-purple-400 text-white text-xl">
                <th className="p-4">Sl. No.</th>
                <th className="p-4">Name</th>
                <th className="p-4">Type</th>
                <th className="p-4">Duration (seconds)</th>
                <th className="p-4">Date & Time</th>
                <th className="p-4">Phone Number</th>
                <th className="p-4 text-center">Actions</th>
              </tr>
            </thead>
            <tbody className="text-gray-700">
              {filteredCallLogs.map((log, index) => (
                <tr
                  key={`${log.timestamp}-${index}`}
                  className="bg-white border-b hover:bg-purple-50"
                >
                  <td className="p-4">{index + 1}</td>
                  <td className="p-4">{log.name || "N/A"}</td>
                  <td className="p-4">{log.type}</td>
                  <td className="p-4">{log.duration}</td>
                  <td className="p-4">{new Date(log.dateTime).toLocaleString()}</td>
                  <td className="p-4">{log.phoneNumber}</td>
                  <td className="p-4 text-center">
                    <button
                      onClick={() => handleDeleteCallLog(log._id)}
                      className="bg-gradient-to-r from-red-500 to-purple-700 text-white px-4 py-2 rounded-lg"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <p className="text-center text-lg text-gray-600">No call logs available</p>
      )}
    </div>
  );
};

// Dashboard with total calls, messages, and recent call logs
const Dashboard = () => {
  const [totalCalls, setTotalCalls] = useState(0);
  const [totalMessages, setTotalMessages] = useState(0);
  const [recentCalls, setRecentCalls] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const callResponse = await axios.get("http://localhost:3005/call-logs");
        setTotalCalls(callResponse.data.length);
        setRecentCalls(callResponse.data.slice(-2).reverse());

        const messageResponse = await axios.get("http://localhost:3005/messages");
        setTotalMessages(messageResponse.data.length);
      } catch (error) {
        console.error("Error fetching dashboard data:", error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="absolute top-20 left-[270px] right-10 p-8 bg-gray-100">
      <h2 className="text-3xl font-extrabold mb-6 text-gray-800">Dashboard</h2>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
        <div className="bg-gradient-to-r from-blue-500 to-blue-600 text-white p-6 rounded-lg shadow-lg">
          <div className="flex items-center">
            <FaPhone className="text-4xl mr-4" />
            <div>
              <h3 className="text-lg font-bold">Total Calls</h3>
              <p className="text-3xl">{totalCalls}</p>
            </div>
          </div>
        </div>

        <div className="bg-gradient-to-r from-green-500 to-green-600 text-white p-6 rounded-lg shadow-lg">
          <div className="flex items-center">
            <FaEnvelope className="text-4xl mr-4" />
            <div>
              <h3 className="text-lg font-bold">Total Messages</h3>
              <p className="text-3xl">{totalMessages}</p>
            </div>
          </div>
        </div>

        <div className="bg-gradient-to-r from-purple-500 to-purple-600 text-white p-6 rounded-lg shadow-lg">
          <div className="flex items-center">
            <FaMapMarkerAlt className="text-4xl mr-4" />
            <div>
              <h3 className="text-lg font-bold">Locations Tracked</h3>
              <p className="text-3xl">5</p>
            </div>
          </div>
        </div>
      </div>

      <RecentActivityTable recentCalls={recentCalls} />
    </div>
  );
};

const AdminPanel = () => (
  <div className="flex">
    <Sidebar />
    <Dashboard />
  </div>
);

export { Dashboard, CallLogTable };
