import React, { useState } from 'react';

const Register = () => {
  const [isLogin, setIsLogin] = useState(true);

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="w-full max-w-md px-8 py-6 bg-white shadow-lg rounded-lg">
        {/* Tabs */}
        <div className="flex justify-around mb-6 border-b-2 border-gray-200">
          <button
            onClick={() => setIsLogin(true)}
            className={`pb-2 text-xl font-semibold ${
              isLogin ? 'border-b-4 border-blue-500 text-blue-500' : 'text-gray-500'
            }`}
          >
            Login
          </button>
          <button
            onClick={() => setIsLogin(false)}
            className={`pb-2 text-xl font-semibold ${
              !isLogin ? 'border-b-4 border-blue-500 text-blue-500' : 'text-gray-500'
            }`}
          >
            Register
          </button>
        </div>

        {/* Form */}
        {isLogin ? (
          <LoginForm />
        ) : (
          <RegisterForm />
        )}
      </div>
    </div>
  );
};

// Login Form Component
const LoginForm = () => (
  <form>
    <div className="mb-4">
      <label className="block text-gray-700">Employee ID</label>
      <input
        type="text"
        placeholder="Enter Employee ID"
        className="w-full px-3 py-2 mt-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
      />
    </div>
    <div className="mb-4">
      <label className="block text-gray-700">Mobile Number</label>
      <input
        type="text"
        placeholder="Enter Mobile Number"
        className="w-full px-3 py-2 mt-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
      />
    </div>
    <button className="w-full py-2 mt-4 text-white bg-blue-500 rounded-lg hover:bg-blue-600">
      Login
    </button>
  </form>
);

// Register Form Component
const RegisterForm = () => (
  <form>
    <div className="mb-4">
      <label className="block text-gray-700">Employee ID</label>
      <input
        type="text"
        placeholder="Enter Employee ID"
        className="w-full px-3 py-2 mt-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
      />
    </div>
    <div className="mb-4">
      <label className="block text-gray-700">Name</label>
      <input
        type="text"
        placeholder="Enter Name"
        className="w-full px-3 py-2 mt-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
      />
    </div>
    <div className="mb-4">
      <label className="block text-gray-700">Mobile Number</label>
      <input
        type="text"
        placeholder="Enter Mobile Number"
        className="w-full px-3 py-2 mt-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
      />
    </div>
    <button className="w-full py-2 mt-4 text-white bg-blue-500 rounded-lg hover:bg-blue-600">
      Register
    </button>
  </form>
);

export default Register;
