import React from 'react';

// Sample data for complaints
const complaints = [
  {
    from: 'John Doe',
    to: 'Manager',
    subject: 'Delay in Project Delivery',
    message: 'The project delivery has been delayed by two weeks due to unforeseen circumstances.',
  },
  {
    from: 'Jane Smith',
    to: 'HR',
    subject: 'Leave Request',
    message: 'I would like to request a leave for two days due to personal reasons.',
  },
  {
    from: 'Mike Johnson',
    to: 'IT Support',
    subject: 'System Issue',
    message: 'My system has been crashing frequently, and I need immediate assistance.',
  },
];

const ComplaintPage = () => {
  const employeeId = 'E123';
  const employeeName = 'John Doe';
  const employeeNumber = '1234567890';

  return (
    <div className="p-6 bg-gray-100 absolute right-0  left-[275px] ">
      {/* Employee Information */}
      <div className="bg-white p-4 rounded-lg shadow-lg mb-6">
        <h2 className="text-2xl font-bold">Employee Information</h2>
        <p className="text-lg">ID: {employeeId}</p>
        <p className="text-lg">Name: {employeeName}</p>
        <p className="text-lg">Number: {employeeNumber}</p>
      </div>

      {/* Complaints List */}
      <h2 className="text-2xl font-bold mb-4">Complaints</h2>
      <div className="space-y-4">
        {complaints.map((complaint, index) => (
          <div key={index} className="bg-white p-4 rounded-lg shadow-lg border-l-4 border-blue-500">
            <h3 className="text-xl font-semibold text-blue-600">{complaint.subject}</h3>
            <p className="text-gray-600">From: <span className="font-semibold">{complaint.from}</span></p>
            <p className="text-gray-600">To: <span className="font-semibold">{complaint.to}</span></p>
            <p className="text-gray-700">{complaint.message}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ComplaintPage;
