import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const AttendanceTable = () => {
  const { employeeId } = useParams();
  const [attendance, setAttendance] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAttendance = async () => {
      setLoading(true); // Start loading

      try {
        const response = await axios.get(`http://localhost:3005/attendance-list`);
        setAttendance(response.data);
      } catch (err) {
        setError("Error fetching attendance data. Please try again later.");
        console.error("Error fetching attendance data:", err);
      } finally {
        setLoading(false); // Stop loading regardless of success or error
      }
    };

    fetchAttendance();
  }, [employeeId]);

  return (
    <div className="p-4 max-w-7xl mx-auto absolute top-16 left-[270px] right-0">
      <h2 className="text-3xl font-bold mb-6 text-center">Attendance Records for Employee ID: {employeeId}</h2>
      {loading ? (
        <p className="text-center text-gray-500">Loading attendance records...</p>
      ) : error ? (
        <p className="text-center text-red-500">{error}</p>
      ) : attendance.length > 0 ? (
        <table className="min-w-full bg-white border border-gray-300 rounded-lg shadow-md">
          <thead className="bg-gray-100">
            <tr>
              <th className="border border-gray-300 px-6 py-3 text-left text-sm font-medium text-gray-600">Date</th>
              <th className="border border-gray-300 px-6 py-3 text-left text-sm font-medium text-gray-600">Status</th>
              <th className="border border-gray-300 px-6 py-3 text-left text-sm font-medium text-gray-600">Remarks</th>
            </tr>
          </thead>
          <tbody>
            {attendance.map((record, index) => (
              <tr key={index} className="hover:bg-gray-100 transition-colors duration-200">
                <td className="border border-gray-300 px-6 py-4">{record.date}</td>
                <td className="border border-gray-300 px-6 py-4">{record.status}</td>
                <td className="border border-gray-300 px-6 py-4">{record.remarks || "N/A"}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p className="text-center text-gray-500">No attendance records available for this employee.</p>
      )}
    </div>
  );
};

export default AttendanceTable;
