import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const Signup = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const auth = localStorage.getItem("user");
    if (auth) {
      navigate("/");
    }
  }, [navigate]);

  const handleSubmit = async () => {
    if (!name || !email || !password) {
      setError(true);
      return false;
    }
    console.log(name, email, password);
    let result = await fetch("http://localhost:5000/register", {
      method: "POST",
      body: JSON.stringify({ name, email, password }),
      headers: {
        "Content-Type": "application/json",
      },
    });
    result = await result.json();
    localStorage.setItem("user", JSON.stringify(result));
    alert("You have successfully signed up");
    if (result) {
      navigate("/");
    }
  };

  return (
    <div className="items-center border border-black flex gap-6 px-12 flex-col absolute top-[20%] left-[30%] bg-gradient-to-tr from-black to-blue-600 rounded-lg">
      <h1 className="text-3xl text-white mt-2 italic font-bold">Register</h1>
      <input
        onChange={(e) => setName(e.target.value)}
        value={name}
        className="w-[30vw] h-10 border border-black rounded-md pl-2"
        type="text"
        placeholder="Enter the Name"
      />
      {error && !name && <span className="text-red-700">Enter Valid Name</span>}
      <input
        onChange={(e) => setEmail(e.target.value)}
        value={email}
        className="w-[30vw] h-10 border border-black rounded-md pl-2"
        type="email"
        placeholder="Enter the Email"
      />
      {error && !email && <span className="text-red-700">Enter Valid Email</span>}
      <input
        onChange={(e) => setPassword(e.target.value)}
        value={password}
        className="w-[30vw] h-10 border rounded-md border-black pl-2"
        type="password"
        placeholder="Enter the Password"
      />
      {error && !password && <span className="text-red-700">Enter Valid Password</span>}

      <button
        onClick={handleSubmit}
        className="border bg-blue-500 hover:bg-blue-800 text-white p-2 rounded-md"
      >
        Signup
      </button>
      <p className="text-white mt-4">
        Already have an account?{" "}
        <span className="underline cursor-pointer text-blue-300" onClick={() => navigate("/login")}>
          Login here
        </span>
      </p>
    </div>
  );
};

export default Signup;
