import React, { useEffect, useState } from 'react';
import axios from 'axios';

const MessagesCard = () => {
  const [messages, setMessages] = useState([]); // State to hold messages data
  const [filteredMessages, setFilteredMessages] = useState([]); // State to hold filtered messages
  const [searchQuery, setSearchQuery] = useState(''); // State for search input

  // Fetch messages from backend API
  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await axios.get('http://localhost:3005/getmessages'); // Replace with your API endpoint
        setMessages(response.data); // Assuming the response contains an array of messages
        setFilteredMessages(response.data); // Initialize filtered messages with fetched data
      } catch (error) {
        console.error('Error fetching messages:', error);
      }
    };

    fetchMessages();
  }, []);

  // Search functionality
  const handleSearch = (e) => {
    const query = e.target.value;
    setSearchQuery(query); // Update search query state

    // Filter messages based on search query
    const filtered = messages.filter(msg => 
      msg.phoneNumber.toLowerCase().includes(query.toLowerCase()) ||
      msg.messageContent.toLowerCase().includes(query.toLowerCase())
    );

    setFilteredMessages(filtered); // Update filtered messages
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Messages</h2>
      <input
        type="text"
        placeholder="Search messages..."
        value={searchQuery}
        onChange={handleSearch}
        className="mb-4 p-2 border rounded"
      />
      <button onClick={handleDeleteAllMessages} className="mb-4 bg-red-500 text-white p-2 rounded">
        Delete All Messages
      </button>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {filteredMessages.length > 0 ? (
          filteredMessages.map((msg) => (
            <div key={msg._id} className="bg-white p-4 rounded-lg shadow-md flex flex-col justify-between">
              <div className="mb-4">
                <p className="text-gray-600 font-semibold">From: {msg.phoneNumber}</p>
                <p className="text-gray-800 mt-2">{msg.messageContent}</p>
              </div>
              <div className="text-gray-500 text-sm">
                <p>Received: {new Date(msg.timestamp).toLocaleDateString()}</p>
                <p>Time: {new Date(msg.timestamp).toLocaleTimeString()}</p>
              </div>
              <button onClick={() => handleDeleteMessage(msg._id)} className="mt-2 bg-red-500 text-white p-1 rounded">
                Delete
              </button>
            </div>
          ))
        ) : (
          <p>No messages available</p>
        )}
      </div>
    </div>
  );
};

export default MessagesCard;
