import React, { useEffect, useState } from 'react';
import axios from 'axios';

const CallLogTable = () => {
  const [callData, setCallData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    const fetchCallLogs = async () => {
      try {
        const response = await axios.get('http://localhost:3005/getcall-log');
        setCallData(response.data);
        setFilteredData(response.data); // Set filteredData initially to display all
      } catch (error) {
        console.error('Error fetching call logs:', error);
      }
    };

    fetchCallLogs();
  }, []);

  const handleSearch = (e) => {
    const query = e.target.value;
    setSearchQuery(query);

    const filtered = callData.filter((call) =>
      call.employeeId.toLowerCase().includes(query.toLowerCase()) ||
      call.callDetails.toLowerCase().includes(query.toLowerCase())
    );

    setFilteredData(filtered);
  };

  return (
    <div className="overflow-x-auto">
      <input
        type="text"
        placeholder="Search by Employee ID or Call Details..."
        className="transition rounded-sm mt-4 border border-blue-700 p-3 w-60"
        value={searchQuery}
        onChange={handleSearch}
      />
      <table className="min-w-full bg-white rounded-lg shadow-md mt-4">
        <thead>
          <tr className="bg-gray-200 text-gray-600 uppercase text-sm leading-normal">
            <th className="py-3 px-6 text-left">Sl. No.</th>
            <th className="py-3 px-6 text-left">Employee ID</th>
            <th className="py-3 px-6 text-left">Call Details</th>
            <th className="py-3 px-6 text-left">Timestamp</th>
          </tr>
        </thead>
        <tbody className="text-gray-600 text-sm font-light">
          {filteredData.length > 0 ? (
            filteredData.map((call, index) => (
              <tr key={call._id} className="border-b border-gray-200 hover:bg-gray-100">
                <td className="py-3 px-6 text-left">{index + 1}</td>
                <td className="py-3 px-6 text-left">{call.employeeId}</td>
                <td className="py-3 px-6 text-left">{call.callDetails}</td>
                <td className="py-3 px-6 text-left">{new Date(call.timestamp).toLocaleString()}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4" className="py-3 px-6 text-center">No matching results found</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default CallLogTable;
