import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const AttendanceDetails = () => {
  const { employeeId } = useParams();
  const [attendanceRecords, setAttendanceRecords] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [filteredAttendance, setFilteredAttendance] = useState([]);
  const [inputEmployeeId, setInputEmployeeId] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [sortConfig, setSortConfig] = useState({ key: "", direction: "asc" });

  // Fetch attendance records from backend API
  const fetchAttendanceRecords = async (id) => {
    try {
      const response = await axios.get(`http://localhost:3005/employees/${id}/attendance-list`);
      setAttendanceRecords(response.data);
      setFilteredAttendance(response.data);
      setErrorMessage(""); // Clear error message if successful
    } catch (error) {
      console.error("Error fetching attendance records:", error);
      setErrorMessage("Error fetching attendance records. Please try again.");
    }
  };

  // Handle delete a single attendance record with confirmation
  const handleDelete = async (attendanceId) => {
    if (window.confirm("Do you really want to delete this attendance record?")) {
      try {
        await axios.delete(`http://localhost:3005/Delete-attendance/${employeeId}/${attendanceId}`);
        setAttendanceRecords((prevRecords) => prevRecords.filter((record) => record._id !== attendanceId));
        setFilteredAttendance((prevFiltered) => prevFiltered.filter((record) => record._id !== attendanceId));
      } catch (error) {
        console.error("Error deleting attendance record:", error);
      }
    }
  };

  // Handle delete all attendance records with confirmation
  const handleDeleteAllAttendanceRecords = async () => {
    if (window.confirm("Do you really want to delete all attendance records? This action cannot be undone.")) {
      try {
        await axios.delete(`http://localhost:3005/Delete-all-attendance/${employeeId}`);
        setAttendanceRecords([]); // Empty the attendance records state
        setFilteredAttendance([]); // Empty the filtered attendance records state
        alert("All attendance records deleted successfully.");
      } catch (error) {
        console.error("Error deleting all attendance records:", error);
        alert("Error deleting all attendance records. Please try again.");
      }
    }
  };

  // Handle search input change
  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  // Handle employee ID input change
  const handleEmployeeIdChange = (event) => {
    setInputEmployeeId(event.target.value);
    setErrorMessage(""); // Clear error message on input change
  };

  // Handle form submission to fetch attendance records for entered Employee ID
  const handleSubmit = (event) => {
    event.preventDefault();
    if (inputEmployeeId.trim() === "") {
      setErrorMessage("Please enter a valid Employee ID.");
      return;
    }
    fetchAttendanceRecords(inputEmployeeId);
    setInputEmployeeId(""); // Clear input after submission
  };

  // Sort attendance records by key
  const sortAttendanceRecords = (key) => {
    let direction = "asc";
    if (sortConfig.key === key && sortConfig.direction === "asc") {
      direction = "desc";
    }
    setSortConfig({ key, direction });

    const sortedRecords = [...filteredAttendance].sort((a, b) => {
      if (a[key] < b[key]) {
        return direction === "asc" ? -1 : 1;
      }
      if (a[key] > b[key]) {
        return direction === "asc" ? 1 : -1;
      }
      return 0;
    });
    setFilteredAttendance(sortedRecords);
  };

  // Filter attendance records based on search term
  useEffect(() => {
    const filtered = attendanceRecords.filter((record) => {
      const date = record.date ? new Date(record.date).toLocaleDateString() : "";
      const status = record.status || "";

      return (
        date.toLowerCase().includes(searchTerm.toLowerCase()) ||
        status.toLowerCase().includes(searchTerm.toLowerCase())
      );
    });
    setFilteredAttendance(filtered);
  }, [searchTerm, attendanceRecords]);

  return (
    <div className="p-4 absolute top-20 right-0 left-[275px]">
      <span className="text-4xl font-bold">Attendance Records {employeeId}</span>
      <form onSubmit={handleSubmit} className="mb-4 mt-6">
        <input
          type="text"
          placeholder="Enter Employee ID"
          value={inputEmployeeId}
          onChange={handleEmployeeIdChange}
          className="border p-2 rounded mr-2 border border-purple-500 focus:border-purple-700 focus:ring-2 focus:ring-purple-500"
        />
        <button
          type="submit"
          className="bg-gradient-to-r from-blue-500 to-blue-700 hover:shadow-lg text-white px-6 py-2 rounded-lg"
        >
          Fetch Attendance Records
        </button>
      </form>
      {errorMessage && <p className="text-red-500">{errorMessage}</p>}

      {attendanceRecords.length > 0 && (
        <div>
          <input
            type="text"
            placeholder="Search attendance records..."
            value={searchTerm}
            onChange={handleSearchChange}
            className="border p-2 rounded mb-4 border border-purple-500 focus:border-purple-700 focus:ring-2 focus:ring-purple-500 w-1/3"
          />
          <button
            onClick={handleDeleteAllAttendanceRecords}
            className="bg-gradient-to-r from-red-500 to-purple-700 hover:shadow-lg text-white px-4 py-2 rounded-lg ml-4"
          >
            Delete All Attendance Records
          </button>
        </div>
      )}

      <h2 className="text-xl font-bold mb-4">Attendance Records for Employee ID: {employeeId}</h2>
      {filteredAttendance.length > 0 ? (
        <table className="min-w-full bg-white border border-gray-300 border-collapse shadow-lg rounded-lg overflow-hidden">
          <thead className="bg-gradient-to-r from-purple-600 to-purple-400 text-white">
            <tr>
              <th className="border border-gray-300 px-6 py-3 text-left font-medium text-white">Sl. No</th>
              <th
                className="border border-gray-300 px-6 py-3 text-left font-medium text-white cursor-pointer"
                onClick={() => sortAttendanceRecords("date")}
              >
                Date
              </th>
              <th
                className="border border-gray-300 px-6 py-3 text-left font-medium text-white cursor-pointer"
                onClick={() => sortAttendanceRecords("status")}
              >
                Status
              </th>
              <th className="border border-gray-300 px-6 py-3 text-left font-medium text-white">Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredAttendance.map((record, index) => (
              <tr key={record._id} className="hover:bg-gray-100 transition-colors duration-200">
                <td className="border border-gray-300 px-6 py-4">{index + 1}</td>
                <td className="border border-gray-300 px-6 py-4">{new Date(record.date).toLocaleDateString()}</td>
                <td className="border border-gray-300 px-6 py-4">{record.status}</td>
                <td className="border border-gray-300 px-6 py-4">
                  <button
                    onClick={() => handleDelete(record._id)}
                    className="bg-gradient-to-r from-red-500 to-purple-700 hover:shadow-lg text-white px-4 py-2 rounded-lg"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No attendance records found for this employee.</p>
      )}
    </div>
  );
};

export default AttendanceDetails;
