// Header.js
import { FaBell, FaSearch, FaUserCircle } from 'react-icons/fa';

const Header = () => {
  return (
    <header className="bg-gradient-to-br from-white to-yellow-400 shadow-md p-4 flex items-center justify-between">
      {/* Branding/Logo - Attica Tracker */}
      <div className="text-2xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-blue-500 to-purple-600 hover:scale-105 transition-transform duration-200">
        Attica Tracker
      </div>

      {/* Search Bar */}
      <div className="hidden md:flex items-center bg-gray-100 rounded-full px-4 py-2 w-1/2">
        <FaSearch className="text-gray-500 mr-2" />
        <input
          type="text"
          placeholder="Search..."
          className="bg-transparent focus:outline-none w-full"
        />
      </div>

      {/* User and Notifications */}
      <div className="flex items-center space-x-4">
        <FaBell className="text-gray-600 text-2xl cursor-pointer hover:text-gray-900" />
        <div className="relative">
          <FaUserCircle className="text-gray-600 text-3xl cursor-pointer hover:text-gray-900" />
        </div>
      </div>
    </header>
  );
};

export default Header;
