import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, Link, useNavigate } from "react-router-dom";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import { BASE_URL } from './constants';

const AttendanceDetails = () => {
  const [employee, setEmployee] = useState(null);
  const [attendance, setAttendance] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [employeeId, setEmployeeId] = useState("");
  const [date, setDate] = useState(new Date());
  const navigate = useNavigate();


  const getStatus = (attendanceStatus) => {
    if (attendanceStatus.isPresent) return "Present";
    if (attendanceStatus.isHalfDay) return "Half Day";
    if (attendanceStatus.isLeave) return "Leave";
    return "Absent"; 
  };
  // Fetch attendance records based on the entered employee ID
  const fetchEmployeeAttendance = async (id) => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(`${BASE_URL}/employees/${id}/attendance-list`);
      setEmployee(response.data);
      setAttendance(response.data.attendanceRecords || []);
    } catch (err) {
      setError("Error fetching employee data. Please try again later.");
      console.error("Error fetching employee data:", err);
    } finally {
      setLoading(false);
    }
  };

  const generateMonthDays = (year, month) => {
    const daysInMonth = new Date(year, month + 1, 0).getDate();
    const days = [];
    for (let day = 1; day <= daysInMonth; day++) {
      days.push(new Date(year, month, day));
    }
    return days;
  };

  const fullMonthAttendance = generateMonthDays(date.getFullYear(), date.getMonth()).map((day) => {
    const record = attendance.find(
      (att) => new Date(att.location.time).toDateString() === day.toDateString()
    );
    let status = null;
    if (record) {
      if (record.AttendanceStatus.isPresent) status = "Present";
      else if (record.AttendanceStatus.isHalfDay) status = "Half Day";
      else if (record.AttendanceStatus.isLeave) status = "Leave";
      else status = "Absent";
    }
    return {
      date: day,
      status: status || "Absent",
    };
  });

  const handleSearch = (e) => {
    e.preventDefault();
    if (employeeId.trim()) {
      fetchEmployeeAttendance(employeeId);
    }
  };
   // Filter only records with attendance and status
   const filteredAttendance = attendance
   .filter((record) => record.AttendanceStatus && record.time && record.photoUri)
   .map((record) => ({
     date: new Date(record.time).toDateString(),
     status: getStatus(record.AttendanceStatus),
     time: new Date(record.time).toLocaleTimeString(), // Get the time in a readable format
     photoUri: record.photoUri,
   }));

  return (
    <div className="p-6 max-w-7xl mx-auto bg-gray-100 rounded-lg shadow-lg mt-20 md:ml-[275px]">
      <h2 className="text-4xl font-bold mb-6 text-center text-blue-600">
        Employee Attendance
      </h2>

      <form onSubmit={handleSearch} className="mb-4 text-center">
        <input
          type="text"
          placeholder="Enter Employee ID"
          value={employeeId}
          onChange={(e) => setEmployeeId(e.target.value)}
          className="border border-gray-300 rounded-md px-4 py-2 w-full sm:w-64 mr-2"
        />
        <button
          type="submit"
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
        >
          Search
        </button>
      </form>

      {employee && (
        <div className="bg-blue-500 text-white p-4 rounded-lg mb-6">
          <h3 className="text-2xl font-semibold">Employee Details</h3>
          <p><strong>ID:</strong> {employee.employeeId}</p>
          <p><strong>Name:</strong> {employee.name}</p>
          <p><strong>userName:</strong> {employee.userName}</p>
          <p><strong>Mobile Number:</strong> {employee.mobileNumber}</p>
          <p><strong>Branch:</strong> {employee.branch}</p>
          <p><strong>Designation:</strong> {employee.designation}</p>
          <p><strong>Selected Month:</strong> {date.toLocaleString("default", { month: "long", year: "numeric" })}</p>
        </div>
      )}

      <div className="flex flex-col md:flex-row justify-center space-x-0 md:space-x-4 mb-6">
        <Link to={`/employees/${employeeId}/attendance-list/leaves`}>
          <button className="px-6 py-2 bg-blue-500 text-white rounded-lg shadow hover:bg-blue-600 transition duration-300 mb-2 md:mb-0">
            View Leave Requests
          </button>
        </Link>
        <Link to={`/employees/${employeeId}/attendance-list/complaints`}>
          <button className="px-6 py-2 bg-red-500 text-white rounded-lg shadow hover:bg-red-600 transition duration-300">
            View Complaints
          </button>
        </Link>
      </div>

      <div className="mb-6">
        <Calendar onChange={setDate} value={date} className="mx-auto" />
        <p className="text-center text-gray-600 mt-2">
          Selected Month: {date.toLocaleString("default", { month: "long", year: "numeric" })}
        </p>
      </div>

      {loading ? (
        <p className="text-center text-gray-500">Loading attendance...</p>
      ) : error ? (
        <p className="text-center text-red-500">{error}</p>
      ) : (
        <div className="mb-8">
          {filteredAttendance.length > 0 ? (
            <table className="min-w-full bg-white mt-4 border border-gray-300 rounded-lg shadow-md">
              <thead className="bg-gray-200">
                <tr>
                  <th className="border border-gray-300 px-4 py-2 text-left text-sm font-medium text-gray-600">Date</th>
                  <th className="border border-gray-300 px-4 py-2 text-left text-sm font-medium text-gray-600">Time</th>
                  <th className="border border-gray-300 px-4 py-2 text-left text-sm font-medium text-gray-600">Status</th>
                  <th className="border border-gray-300 px-4 py-2 text-left text-sm font-medium text-gray-600">Photo</th>
                </tr>
              </thead>
              <tbody>
                {filteredAttendance.map(({ date, status, time, photoUri }) => (
                  <tr key={date} className="hover:bg-gray-100 transition-colors duration-200">
                    <td className="border border-gray-300 px-4 py-2 text-gray-800">{date}</td>
                    <td className="border border-gray-300 px-4 py-2 text-gray-800">{time}</td>
                    <td className="border border-gray-300 px-4 py-2 text-gray-800">{status}</td>
                    <td className="border border-gray-300 px-4 py-2">
                      {/* Uncomment this line to show the photo */}
                      {/* <img src={photoUri} alt="Attendance" className="w-16 h-16 object-cover rounded" /> */}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p className="text-center text-gray-500">No attendance records for this period.</p>
          )}
        </div>
      )}
      </div>
      );
      };

export default AttendanceDetails;
