import React, { useEffect, useState } from "react";
import axios from "axios";

const LeaveForm = ({ employeeId }) => {
  const [leaveRequests, setLeaveRequests] = useState([]);
  const [employee, setEmployee] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch employee data
  useEffect(() => {
    const fetchEmployee = async () => {
      setLoading(true);
      try {
        const employeeResponse = await axios.get(
          `http://localhost:3005/employees/${employeeId}/attendance-list/leaves`
        );
        setEmployee(employeeResponse.data);
      } catch (err) {
        setError("Error fetching employee details.");
      } finally {
        setLoading(false);
      }
    };

    const fetchLeaveRequests = async () => {
      try {
        const leaveResponse = await axios.get(
          `http://localhost:3005/employees/${employeeId}/attendance-list/leaves`
        );
        setLeaveRequests(leaveResponse.data);
      } catch (err) {
        setError("Error fetching leave requests.");
      }
    };

    fetchEmployee();
    fetchLeaveRequests();
  }, [employeeId]);

  return (
    <div className="p-6 bg-gradient-to-b from-blue-500 to-purple-500 min-h-screen absolute top-10 right-10 left-[275px]">
      {/* Employee Information */}
      <div className="bg-gradient-to-r from-green-400 to-blue-400 p-4 rounded-lg shadow-lg mb-6">
        <h2 className="text-2xl font-bold text-white">Employee Information</h2>
        <p className="text-lg text-white">ID: {employee.employeeId || employeeId}</p>
        <p className="text-lg text-white">Name: {employee.name}</p>
        <p className="text-lg text-white">Number: {employee.mobileNumber}</p>
      </div>

      {/* Leave Requests Section */}
      <h2 className="text-3xl font-bold text-white text-center mb-6">
        Leave Requests Inbox
      </h2>

      {loading ? (
        <p className="text-center text-white">Loading...</p>
      ) : error ? (
        <p className="text-center text-red-600">{error}</p>
      ) : leaveRequests.length > 0 ? (
        <div className="space-y-4">
          {leaveRequests.map((request, index) => (
            <div
              key={index}
              className="bg-white p-4 rounded-lg shadow-lg border-l-4 border-blue-500"
            >
              <h3 className="text-xl font-semibold text-blue-600">
                {request.reason} ({request.leaveType})
              </h3>
              <p className="text-gray-600">
                From: <span className="font-semibold">{request.from}</span>
              </p>
              <p className="text-gray-600">
                To: <span className="font-semibold">{request.to}</span>
              </p>
              <p className="text-gray-700">Start Date: {request.startDate}</p>
              <p className="text-gray-700">End Date: {request.endDate}</p>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-center text-white">No leave requests found.</p>
      )}
    </div>
  );
};

export default LeaveForm;
