import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const Complaints = () => {
  const { employeeId,name,mobileNumber } = useParams(); // Get the employeeId from the URL
  const [employee, setEmployee] = useState({});
  const [complaints, setComplaints] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        // Fetch employee details
        const employee = await axios.get(`http://localhost:3005/employees/${employeeId}`);
        setEmployee(employee.data);

        // Fetch complaints for the employee
        const complaints = await axios.get(`http://localhost:3005/employees/${employeeId}/attendance-list/complaints`);
        setComplaints(complaints.data);
      } catch (err) {
        setError("Error fetching data. Please try again later.");
        console.error("Error fetching data:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [employeeId]); // Dependency ensures this effect runs when employeeId changes

  return (
    <div className="p-6 bg-gray-100 absolute top-20 right-0 left-[275px]">
      {/* Employee Information */}
      <div className="bg-gradient-to-r from-green-300 to-blue-300 p-4 rounded-lg shadow-lg mb-6">
        <h2 className="text-2xl font-bold text-white">Employee Information</h2>
        <p className="text-lg text-white">ID: {employee.employeeId || employeeId}</p>
        <p className="text-lg text-white">Name: {employee.name|| name}</p>
        <p className="text-lg text-white">Number: {employee.mobileNumber}</p>
      </div>

      {/* Complaints List */}
      <h2 className="text-2xl font-bold mb-4 text-blue-600">Complaints</h2>
      <div className="space-y-4">
        {loading ? (
          <p className="text-center text-gray-500">Loading complaints...</p>
        ) : error ? (
          <p className="text-center text-red-500">{error}</p>
        ) : complaints.length > 0 ? (
          complaints.map((complaint, index) => (
            <div key={index} className="bg-white p-4  rounded-lg shadow-lg border-l-4 border-purple-500">
         <span className="text-xl">Title:</span>     <h3 className="text-xl font-semibold  mb-6">{complaint.title}</h3>
      
              <span className="text-xl">Description:</span>  <p className="text-black text-xl">{complaint.description}</p>
            </div>
          ))
        ) : (
          <p className="text-center text-gray-500">No complaints found.</p>
        )}
      </div>
    </div>
  );
};

export default Complaints;
