import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, Link } from "react-router-dom";
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

const AttendanceTable = () => {
  const [employee, setEmployee] = useState(null);  // Single employee
  const [attendance, setAttendance] = useState([]);  // Store attendance records, initialized as empty array
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { employeeId } = useParams();  // Get the employeeId from the URL
  const [date, setDate] = useState(new Date());  // Selected date (to filter month)
  
  useEffect(() => {
    const fetchEmployeeAttendance = async () => {
      setLoading(true);
      try {
        // Fetch the attendance data for a single employee based on employeeId
        const response = await axios.get(`http://localhost:3005/employees/${employeeId}/attendance-list`);
        setEmployee(response.data);  // Set employee data
        setAttendance(response.data.attendanceRecords || []);  // Set attendance records, ensure it's an array
      } catch (err) {
        setError("Error fetching employee data. Please try again later.");
        console.error("Error fetching employee data:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchEmployeeAttendance();
  }, [employeeId]);  // Dependency on employeeId to refetch data when it changes

  // Filter attendance based on the selected month and year
  const filteredAttendance = Array.isArray(attendance)
    ? attendance.filter((record) => {
        const recordDate = new Date(record.date);
        return (
          recordDate.getFullYear() === date.getFullYear() &&
          recordDate.getMonth() === date.getMonth()
        );
      })
    : [];

  return (
    <div className="p-6 max-w-7xl mx-auto bg-gray-100 rounded-lg shadow-lg absolute top-20 right-10 left-[275px]">
      <h2 className="text-4xl font-bold mb-6 text-center text-blue-600">Employee Attendance</h2>
      
      {/* Calendar Component */}
      <div className="mb-6">
        <Calendar 
          onChange={setDate} 
          value={date} 
          className="mx-auto" 
        />
        <p className="text-center text-gray-600 mt-2">Selected Date: {date.toDateString()}</p>
      </div>

      {loading ? (
        <p className="text-center text-gray-500">Loading attendance...</p>
      ) : error ? (
        <p className="text-center text-red-500">{error}</p>
      ) : employee ? (
        <div className="mb-8">
          {/* Navigation Buttons */}
          <div className="mt-4 mb-4 text-right">
            <Link to='./leaves'>
              <button className="bg-red-500 mr-4 hover:bg-red-700 text-white font-bold py-2 px-4 rounded shadow-lg">Leave Requests</button>
            </Link>
            <Link to='./complaints'>
              <button className="bg-yellow-500 hover:bg-yellow-700 text-white font-bold py-2 px-4 rounded">
                Complaints
              </button>
            </Link>
          </div>
          
          {/* Employee Details */}
          <div className="bg-blue-500 text-white p-4 rounded-lg">
            <h3 className="text-2xl font-semibold">Employee Details</h3>
            <p><strong>ID:</strong> {employee.employeeId}</p>
            <p><strong>Name:</strong> {employee.name}</p>
            <p><strong>Mobile Number:</strong> {employee.mobileNumber}</p>
            <p><strong>Selected Date:</strong> {date.toDateString()}</p>
          </div>

          {/* Attendance Table */}
          <table className="min-w-full bg-white mt-4 border border-gray-300 rounded-lg shadow-md">
            <thead className="bg-gray-200">
              <tr>
                <th className="border border-gray-300 px-4 py-2 text-left text-sm font-medium text-gray-600">Date</th>
                <th className="border border-gray-300 px-4 py-2 text-left text-sm font-medium text-gray-600">Status</th>
              </tr>
            </thead>
            <tbody>
              {/* Use filtered attendance from backend */}
              {filteredAttendance.map((record) => {
                const recordDate = new Date(record.date);
                const day = recordDate.getDate();

                return (
                  <tr key={day} className="hover:bg-gray-100 transition-colors duration-200">
                    <td className="border border-gray-300 px-4 py-2">
                      {recordDate.toDateString()} {/* Display real date from backend */}
                    </td>
                    <td className="border border-gray-300 px-4 py-2">
                      <span className={`inline-block px-3 py-1 rounded-full text-sm font-bold ${
                        record.status === "Present" ? "bg-green-100 text-green-600" : "bg-red-100 text-red-600"
                      }`}>
                        {record.status} {/* Display status from backend */}
                      </span>
                    </td>
                  </tr>
                );
              })}
              
              {/* If no data exists for specific dates */}
              {filteredAttendance.length === 0 && (
                <tr>
                  <td colSpan="2" className="text-center text-gray-500 p-4">
                    No attendance records found for the selected month.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      ) : (
        <p className="text-center text-gray-500">Employee not found.</p>
      )}
    </div>
  );
};

export default AttendanceTable;
