import React, { useEffect, useState } from 'react';
import axios from 'axios';

const MessagesCard = () => {
  const [messages, setMessages] = useState([]); // State to hold messages data
  const [filteredMessages, setFilteredMessages] = useState([]); // State for search filter
  const [searchQuery, setSearchQuery] = useState(''); // State for search query

  // Fetch messages from backend API
  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await axios.get('http://localhost:3005/getmessages');
        setMessages(response.data);
        setFilteredMessages(response.data); // Initialize filtered messages with all data
      } catch (error) {
        console.error('Error fetching messages:', error);
      }
    };

    fetchMessages();
  }, []);

  // Handle search input
  const handleSearch = (e) => {
    const query = e.target.value;
    setSearchQuery(query);

    const filtered = messages.filter((msg) =>
      msg.phoneNumber.toLowerCase().includes(query.toLowerCase()) ||
      msg.messageContent.toLowerCase().includes(query.toLowerCase())
    );
    setFilteredMessages(filtered);
  };

  // Function to delete a single message
  const handleDeleteMessage = async (id) => {
    const confirmDelete = window.confirm("Do you really want to delete this message?");
    if (confirmDelete) {
      try {
        await axios.delete(`http://localhost:3005/delete-message/${id}`);
        setMessages(messages.filter((msg) => msg._id !== id)); // Update local state
        setFilteredMessages(filteredMessages.filter((msg) => msg._id !== id)); // Update filtered state
      } catch (error) {
        console.error('Error deleting message:', error);
      }
    }
  };

  // Function to delete all messages
  const handleDeleteAllMessages = async () => {
    const confirmDelete = window.confirm("Do you really want to delete all messages?");
    if (confirmDelete) {
      try {
        await axios.delete('http://localhost:3005/delete-all-messages');
        setMessages([]); // Clear local state
        setFilteredMessages([]); // Clear filtered state
      } catch (error) {
        console.error('Error deleting all messages:', error);
      }
    }
  };

  return (
    <div className="p-4">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold mb-4">Messages</h2>
        <button
          onClick={handleDeleteAllMessages}
          className="bg-red-500 text-white px-4 py-2 rounded"
        >
          Delete All Messages
        </button>
      </div>
      <input
        type="text"
        placeholder="Search by Phone Number or Message Content"
        className="border border-gray-400 p-2 rounded mb-4 w-full"
        value={searchQuery}
        onChange={handleSearch}
      />
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {filteredMessages.length > 0 ? (
          filteredMessages.map((msg) => (
            <div
              key={msg._id}
              className="bg-white p-4 rounded-lg shadow-md flex flex-col justify-between"
            >
              <div className="mb-4">
                <p className="text-gray-600 font-semibold">From: {msg.phoneNumber}</p>
                <p className="text-gray-800 mt-2">{msg.messageContent}</p>
              </div>
              <div className="text-gray-500 text-sm">
                <p>Received: {new Date(msg.timestamp).toLocaleDateString()}</p>
                <p>Time: {new Date(msg.timestamp).toLocaleTimeString()}</p>
              </div>
              <button
                onClick={() => handleDeleteMessage(msg._id)}
                className="bg-red-500 text-white px-2 py-1 rounded mt-4"
              >
                Delete
              </button>
            </div>
          ))
        ) : (
          <p>No messages available</p>
        )}
      </div>
    </div>
  );
};

export default MessagesCard;
