import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import { BASE_URL } from './constants';

const UserRequest = () => {
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const fetchEmployees = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(`${BASE_URL}/employees`);
      // Assuming the response has an `approvalStatus` field for each employee
      setEmployees(response.data);
    } catch (err) {
      setError("Error fetching employees. Please try again later.");
      console.error("Error fetching employees:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchEmployees();
  }, []);

  const handleApprove = (employeeId) => {
    setEmployees(prevEmployees =>
      prevEmployees.map(employee =>
        employee._id === employeeId
          ? { ...employee, approvalStatus: 'approved' } // Update status to approved
          : employee
      )
    );
  };

  const handleReject = (employeeId) => {
    setEmployees(prevEmployees =>
      prevEmployees.map(employee =>
        employee._id === employeeId
          ? { ...employee, approvalStatus: 'rejected' } // Update status to rejected
          : employee
      )
    );
  };

  const EmployeeRow = ({ employee, index }) => (
    <tr key={employee._id} className="hover:bg-gray-100 transition-colors duration-200">
      <td className="border border-gray-300 px-4 py-2 text-center">{index + 1}</td>
      <td className="border border-gray-300 px-4 py-2">{employee.employeeId}</td>
      <td className="border border-gray-300 px-4 py-2">{employee.name} 
        {employee.approvalStatus === 'pending' && <span className="text-orange-500"> (Pending)</span>}
        {employee.approvalStatus === 'approved' && <span className="text-green-500"> (Approved)</span>}
        {employee.approvalStatus === 'rejected' && <span className="text-red-500"> (Rejected)</span>}
      </td>
      <td className="border border-gray-300 px-4 py-2">{employee.mobileNumber}</td>
      <td className="border border-gray-300 px-4 py-2">
        <button onClick={() => navigate(`/employees/${employee.employeeId}/details`)} className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-1 px-2 rounded mr-2" aria-label="View call logs">View</button>
      </td>
      <td className="border border-gray-300 px-4 py-2 flex items-center justify-center">
        {employee.approvalStatus === 'pending' && (
          <>
            <button onClick={() => handleApprove(employee._id)} className='bg-green-500 hover:bg-green-700 text-white font-bold py-1 px-2 rounded mr-2'>Approve</button>
            <button onClick={() => handleReject(employee._id)} className='bg-red-500 hover:bg-red-700 text-white font-bold py-1 px-2 rounded'>Reject</button>
          </>
        )}
      </td>
    </tr>
  );

  return (
    <div className="p-4 w-full max-w-5xl mx-auto mt-8">
      <h2 className="text-3xl font-bold mb-6 text-center">Employee List</h2>

      {loading ? (
        <div className="text-center">
          <p className="text-gray-500">Loading employees...</p>
        </div>
      ) : error ? (
        <div className="text-center text-red-500">
          <p>{error}</p>
          <button onClick={fetchEmployees} className="mt-4 bg-red-500 text-white px-4 py-2 rounded hover:bg-red-700">Retry</button>
        </div>
      ) : employees.length > 0 ? (
        <div className="overflow-x-auto">
          <table className="w-full bg-white border border-gray-300 rounded-lg shadow-md">
            <thead className="bg-gray-100">
              <tr>
                <th className="border border-gray-300 px-4 py-3 text-left text-sm font-medium text-gray-600">SL No</th>
                <th className="border border-gray-300 px-4 py-3 text-left text-sm font-medium text-gray-600">Employee ID</th>
                <th className="border border-gray-300 px-4 py-3 text-left text-sm font-medium text-gray-600">Employee Name</th>
                <th className="border border-gray-300 px-4 py-3 text-left text-sm font-medium text-gray-600">Mobile Number</th>
                <th className="border border-gray-300 px-4 py-3 text-left text-sm font-medium text-gray-600">Details</th>
                <th className="border border-gray-300 px-4 py-3 text-left text-sm font-medium text-gray-600">Actions</th>
              </tr>
            </thead>
            <tbody>
              {employees.filter(employee => employee.approvalStatus === 'pending').map((employee, index) => (
                <EmployeeRow key={employee._id} employee={employee} index={index} />
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <div className="text-center">
          <p className="text-gray-500">No employees available.</p>
        </div>
      )}
    </div>
  );
};

export default UserRequest;
