import React from 'react';

const AttendanceTable = () => {
  // Array of dates for the attendance table
  const dates = Array.from({ length: 31 }, (_, i) => i + 1); // Dates from 1 to 31
  // Sample empty attendance data
  const attendanceData = dates.map((date) => ({
    date,
    employeeId: '',
    employeeName: '',
    mobileNumber: '',
    status: '', // 'Present' or 'Absent'
    salary: '',
  }));

  return (
    <div className="p-4 max-w-7xl mx-auto mt-10 absolute left-[270px]">
      <h2 className="text-3xl font-bold mb-6 text-center">Attendance List</h2>
      <table className="min-w-full bg-white border border-gray-300 rounded-lg shadow-md">
        <thead className="bg-gray-100">
          <tr>
            <th className="border border-gray-300 px-6 py-3 text-left text-sm font-medium text-gray-600">SL No</th>
            <th className="border border-gray-300 px-6 py-3 text-left text-sm font-medium text-gray-600">Date</th>
            <th className="border border-gray-300 px-6 py-3 text-left text-sm font-medium text-gray-600">Employee ID</th>
            <th className="border border-gray-300 px-6 py-3 text-left text-sm font-medium text-gray-600">Employee Name</th>
            <th className="border border-gray-300 px-6 py-3 text-left text-sm font-medium text-gray-600">Mobile Number</th>
            <th className="border border-gray-300 px-6 py-3 text-left text-sm font-medium text-gray-600">Attendance Status</th>
            <th className="border border-gray-300 px-6 py-3 text-left text-sm font-medium text-gray-600">Salary</th>
          </tr>
        </thead>
        <tbody>
          {attendanceData.map((record, index) => (
            <tr key={index} className="hover:bg-gray-100 transition-colors duration-200">
              <td className="border border-gray-300 px-6 py-4">{index + 1}</td>
              <td className="border border-gray-300 px-6 py-4">{record.date}</td>
              <td className="border border-gray-300 px-6 py-4">{record.employeeId || '-'}</td>
              <td className="border border-gray-300 px-6 py-4">{record.employeeName || '-'}</td>
              <td className="border border-gray-300 px-6 py-4">{record.mobileNumber || '-'}</td>
              <td className="border border-gray-300 px-6 py-4">
                <span className={`font-bold ${record.status === 'Present' ? 'text-green-500' : 'text-red-500'}`}>
                  {record.status || '-'}
                </span>
              </td>
              <td className="border border-gray-300 px-6 py-4">{record.salary || '-'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AttendanceTable;
