import React, { useEffect, useState } from "react";
import axios from "axios";

const LeaveForm = () => {
  const [leaveRequests, setLeaveRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect((employeeId) => {
    const fetchLeaveRequests = async () => {
      setLoading(true);
      try {
        const response = await axios.get(`http://localhost:3005/employees/${employeeId}/attendance-list/leaves`);
        setLeaveRequests(response.data);
      } catch (err) {
        setError("Error fetching leave requests. Please try again later.");
      } finally {
        setLoading(false);
      }
    };

    fetchLeaveRequests();
  }, []);

  return (
    <div className="p-6 bg-gradient-to-b from-blue-500 to-purple-500 min-h-screen absolute top-20 left-[275px] right-6">
      <h2 className="text-3xl font-bold text-white text-center mb-6">
        Leave Requests Inbox
      </h2>

      {loading ? (
        <p className="text-center text-white">Loading leave requests...</p>
      ) : error ? (
        <p className="text-center text-red-600">{error}</p>
      ) : leaveRequests.length > 0 ? (
        <div className="space-y-4">
          {leaveRequests.map((request, index) => (
            <div
              key={index}
              className="bg-white p-4 rounded-lg shadow-lg border-l-4 border-blue-500"
            >
              <h3 className="text-xl font-semibold text-blue-600">
                {request.reason} ({request.type})
              </h3>
              <p className="text-gray-600">
                From: <span className="font-semibold">{request.from}</span>
              </p>
              <p className="text-gray-600">
                To: <span className="font-semibold">{request.to}</span>
              </p>
              <p className="text-gray-700">Start Date: {request.startdate}</p>
              <p className="text-gray-700">End Date: {request.enddate}</p>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-center text-white">No leave requests found.</p>
      )}
    </div>
  );
};

export default LeaveForm;
