import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";


const LeaveForm = () => {
  const { employeeId,name,mobileNumber } = useParams(); // Get the employeeId from the URL
  const [leaveRequests, setLeaveRequests] = useState([]);
  const [employee, setEmployee] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch employee and leave requests data
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        // Fetch employee details based on the provided employeeId
        const employee = await axios.get("http://localhost:3005/employees");
        setEmployee(employee.data);

        // Fetch leave requests for the specific employee
        const leaveRequests = await axios.get(`http://localhost:3005/employees/${employeeId}/attendance-list/leaves`);
        useEffect(() => {
          const fetchData = async () => {
            setLoading(true);
            try {
              const leaveRequests = await axios.get(`http://localhost:3005/employees/${employeeId}/attendance-list/leaves`);
              console.log(leaveRequests.data);  // Add this to check the response structure
              setLeaveRequests(leaveRequests.data);
            } catch (err) {
              setError("Error fetching data. Please try again later.");
            } finally {
              setLoading(false);
            }
          };
          fetchData();
        }, [employeeId]);
        
        
        setLeaveRequests(leaveRequests.data);
      } catch (err) {
        setError("Error fetching data. Please try again later.");
        console.error("Error fetching data:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [employeeId]);

  return (
    <div className="p-6 bg-gradient-to-b  min-h-screen absolute top-20 right-10 left-[275px]">
      {/* Employee Information */}
      <div className="bg-gradient-to-r from-green-400 to-blue-400 p-4 rounded-lg shadow-lg mb-6">
        <h2 className="text-2xl font-bold text-white">Employee Information</h2>
        <p className="text-lg text-white">
          ID: {employee.employeeId || employeeId}
        </p>
        <p className="text-lg text-white">Name: {employee.name || "N/A"}</p>
        <p className="text-lg text-white">
          Number: {employee.mobileNumber || "N/A"}
        </p>
      </div>

      {/* Leave Requests Section */}
      <h2 className="text-3xl font-bold text-black text-center mb-6">
        Leave Requests Inbox
      </h2>

      {loading ? (
        <p className="text-center text-white">Loading...</p>
      ) : error ? (
        <p className="text-center text-red-600">{error}</p>
      ) : leaveRequests.length > 0 ? (
        <div className="space-y-4">
          {leaveRequests.map((request, index) => (
            <div
              key={index}
              className="bg-white p-4 rounded-lg shadow-lg border-l-4 border-blue-500"
            >
              <h3 className="text-xl font-semibold text-blue-600">
                {request.reason} ({request.leaveType})
              </h3>
              <p className="text-gray-600">
                From: <span className="font-semibold">{request.from}</span>
              </p>
              <p className="text-gray-600">
                To: <span className="font-semibold">{request.to}</span>
              </p>
              <p className="text-gray-700">Start Date: {request.startDate}</p>
              <p className="text-gray-700">End Date: {request.endDate}</p>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-center text-white">No leave requests found.</p>
      )}
    </div>
  );
};

export default LeaveForm;
