import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, Link } from "react-router-dom";
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

const AttendanceDetails = () => {
  const [employee, setEmployee] = useState(null);
  const [attendance, setAttendance] = useState([]);  // State for attendance data
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const { employeeId } = useParams();
  const [date, setDate] = useState(new Date());
  const [daysInMonth, setDaysInMonth] = useState([]);
  const [inputEmployeeId, setInputEmployeeId] = useState(employeeId || "");  // State for search bar input
  const [searchError, setSearchError] = useState("");  // For handling invalid Employee ID errors

  // Function to fetch employee and attendance data
  const fetchEmployeeAttendance = async (id) => {
    setLoading(true);
    try {
      // Fetch the employee data
      const employeeResponse = await axios.get(`http://localhost:3005/employees/${id}`);
      setEmployee(employeeResponse.data);

      // Fetch attendance data for the employee for the selected month and year
      const attendanceResponse = await axios.get(
        `http://localhost:3005/employees/${id}/attendance-list?year=${date.getFullYear()}&month=${date.getMonth() + 1}`
      );
      setAttendance(attendanceResponse.data);  // Attendance data for the month
      setSearchError("");  // Clear any search errors
      setError(null);  // Clear general error if successful
    } catch (err) {
      setEmployee(null);
      setAttendance([]);
      setSearchError("Error fetching employee or attendance data. Please check the Employee ID.");
      console.error("Error fetching employee or attendance data:", err);
    } finally {
      setLoading(false);
    }
  };

  // Handle search input change
  const handleInputChange = (event) => {
    setInputEmployeeId(event.target.value);
    setSearchError("");  // Clear any previous search errors
  };

  // Handle search submit
  const handleSearchSubmit = (event) => {
    event.preventDefault();
    if (inputEmployeeId.trim() === "") {
      setSearchError("Please enter a valid Employee ID.");
      return;
    }
    fetchEmployeeAttendance(inputEmployeeId);
  };

  // Get the days of the selected month
  useEffect(() => {
    const getDaysInMonth = (year, month) => {
      return new Date(year, month + 1, 0).getDate();  // Last day of the month
    };

    const year = date.getFullYear();
    const month = date.getMonth();
    const days = getDaysInMonth(year, month);
    setDaysInMonth(Array.from({ length: days }, (_, i) => i + 1));  // Create array of days
  }, [date]);

  // Find attendance status for a specific day
  const getAttendanceForDay = (day) => {
    const formattedDate = `${date.getFullYear()}-${(date.getMonth() + 1).toString().padStart(2, '0')}-${day.toString().padStart(2, '0')}`;
    const attendanceRecord = attendance.find(att => att.date === formattedDate);
    if (attendanceRecord) {
      return {
        status: attendanceRecord.status,
        reason: attendanceRecord.reason || "",
      };
    }
    return { status: "Absent", reason: "No record" };  // Default to "Absent" if no record exists
  };

  return (
    <div className="p-6 max-w-7xl mx-auto bg-gray-100 rounded-lg shadow-lg absolute top-20 right-10 left-[275px]">
      <h2 className="text-4xl font-bold mb-6 text-center text-blue-600">Employee Attendance</h2>

      {/* Search Bar */}
      <form onSubmit={handleSearchSubmit} className="mb-6 text-center">
        <input
          type="text"
          placeholder="Enter Employee ID"
          value={inputEmployeeId}
          onChange={handleInputChange}
          className="border p-2 rounded mr-2 focus:outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-500"
        />
        <button
          type="submit"
          className="bg-blue-500 text-white font-bold py-2 px-4 rounded-lg hover:bg-blue-700"
        >
          Search
        </button>
      </form>
      {searchError && <p className="text-red-500 text-center">{searchError}</p>}

      {/* Calendar Component */}
      <div className="mb-6">
        <Calendar 
          onChange={setDate} 
          value={date} 
          className="mx-auto" 
        />
        <p className="text-center text-gray-600 mt-2">Selected Date: {date.toDateString()}</p>
      </div>

      {loading ? (
        <p className="text-center text-gray-500">Loading attendance...</p>
      ) : error ? (
        <p className="text-center text-red-500">{error}</p>
      ) : employee ? (
        <div className="mb-8">
          {/* Links for Leaves and Complaints */}
          <div className="mt-4 mb-4 text-right">
            <Link to='./leaves'>
              <button className="bg-red-500 mr-4 hover:bg-red-700 text-white font-bold py-2 px-4 rounded shadow-lg">Leave Requests</button>
            </Link>
            <Link to='./complaints'>
              <button className="bg-yellow-500 hover:bg-yellow-700 text-white font-bold py-2 px-4 rounded">
                Complaints
              </button>
            </Link>
          </div>

          {/* Employee Details */}
          <div className="bg-blue-500 text-white p-4 rounded-lg">
            <h3 className="text-2xl font-semibold">Employee Details</h3>
            <p><strong>ID:</strong> {employee.employeeId}</p>
            <p><strong>Name:</strong> {employee.name}</p>
            <p><strong>Mobile Number:</strong> {employee.mobileNumber}</p>
            <p><strong>Selected Date:</strong> {date.toDateString()}</p>
          </div>

          {/* Attendance Table */}
          <table className="min-w-full bg-white mt-4 border border-gray-300 rounded-lg shadow-md">
            <thead className="bg-gray-200">
              <tr>
                <th className="border border-gray-300 px-4 py-2 text-left text-sm font-medium text-gray-600">Date</th>
                <th className="border border-gray-300 px-4 py-2 text-left text-sm font-medium text-gray-600">Status</th>
                <th className="border border-gray-300 px-4 py-2 text-left text-sm font-medium text-gray-600">Reason for Leave</th>
              </tr>
            </thead>
            <tbody>
              {daysInMonth.map((day) => {
                const { status, reason } = getAttendanceForDay(day);
                return (
                  <tr key={day} className="hover:bg-gray-100 transition-colors duration-200">
                    <td className="border border-gray-300 px-4 py-2">{day}</td>
                    <td className="border border-gray-300 px-4 py-2">
                      <span className={`inline-block px-3 py-1 rounded-full text-sm font-bold ${
                        status === "Present" ? "bg-green-100 text-green-600" : "bg-red-100 text-red-600"
                      }`}>
                        {status}
                      </span>
                    </td>
                    <td className="border border-gray-300 px-4 py-2">
                      {status === "Absent" && reason && (
                        <span className="text-red-500">{reason}</span>
                      )}
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      ) : (
        <p className="text-center text-gray-500">Employee not found.</p>
      )}
    </div>
  );
};

export default AttendanceDetails;
