import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, Link } from "react-router-dom";
import Calendar from 'react-calendar';  
import 'react-calendar/dist/Calendar.css';  

const AttendanceTable = () => {
  const [employee, setEmployee] = useState(null);  
  const [attendanceData, setAttendanceData] = useState([]);  // Attendance data for the month
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { employeeId } = useParams();  
  const [date, setDate] = useState(new Date());  

  useEffect(() => {
    const fetchEmployeeAttendance = async () => {
      setLoading(true);
      try {
        // Fetch employee data
        const response = await axios.get(`http://localhost:3005/employees/${employeeId}`);
        setEmployee(response.data); 

        // Fetch attendance data for the whole month
        const startOfMonth = new Date(date.getFullYear(), date.getMonth(), 1);
        const endOfMonth = new Date(date.getFullYear(), date.getMonth() + 1, 0);
        
        const attendanceResponse = await axios.get(`http://localhost:3005/employees/${employeeId}/attendance`, {
          params: {
            start: startOfMonth.toISOString(),
            end: endOfMonth.toISOString()
          }
        });
        
        setAttendanceData(attendanceResponse.data);  // Set attendance data
      } catch (err) {
        setError("Error fetching data. Please try again later.");
        console.error("Error fetching data:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchEmployeeAttendance();
  }, [employeeId, date]);  // Update when employeeId or date changes

  return (
    <div className="p-6 max-w-7xl mx-auto bg-gray-100 rounded-lg shadow-lg absolute top-20 right-10 left-[275px]">
      <h2 className="text-4xl font-bold mb-6 text-center text-blue-600">Employee Attendance</h2>
      
      <div className="mb-6">
        <Calendar 
          onChange={setDate} 
          value={date} 
          className="mx-auto" 
        />
        <p className="text-center text-gray-600 mt-2">Selected Month: {date.toLocaleString('default', { month: 'long', year: 'numeric' })}</p>
      </div>

      {loading ? (
        <p className="text-center text-gray-500">Loading attendance...</p>
      ) : error ? (
        <p className="text-center text-red-500">{error}</p>
      ) : employee ? (
        <div className="mb-8">
          <div className="mt-4 mb-4 text-right">
            <Link to='./leaves'>
              <button className="bg-red-500 mr-4 hover:bg-red-700 text-white font-bold py-2 px-4 rounded shadow-lg">Leave Requests</button>
            </Link>
            <Link to='./complaints'>
              <button className="bg-yellow-500 hover:bg-yellow-700 text-white font-bold py-2 px-4 rounded">Complaints</button>
            </Link>
          </div>

          <div className="bg-blue-500 text-white p-4 rounded-lg">
            <h3 className="text-2xl font-semibold">Employee Details</h3>
            <p><strong>ID:</strong> {employee.employeeId}</p>
            <p><strong>Name:</strong> {employee.name}</p>
            <p><strong>Mobile Number:</strong> {employee.mobileNumber}</p>
          </div>

          {/* Attendance Table */}
          <table className="min-w-full bg-white mt-4 border border-gray-300 rounded-lg shadow-md">
            <thead className="bg-gray-200">
              <tr>
                <th className="border border-gray-300 px-4 py-2 text-left text-sm font-medium text-gray-600">Date</th>
                <th className="border border-gray-300 px-4 py-2 text-left text-sm font-medium text-gray-600">Status</th>
                <th className="border border-gray-300 px-4 py-2 text-left text-sm font-medium text-gray-600">Location</th>
              </tr>
            </thead>
            <tbody>
              {Array.from({ length: new Date(date.getFullYear(), date.getMonth() + 1, 0).getDate() }, (_, i) => {
                const currentDate = new Date(date.getFullYear(), date.getMonth(), i + 1);
                const attendanceRecord = attendanceData.find(record => {
                  const attendanceDate = new Date(record.AttendanceStatus.time);
                  return attendanceDate.toDateString() === currentDate.toDateString();
                });

                return (
                  <tr key={currentDate} className="hover:bg-gray-100 transition-colors duration-200">
                    <td className="border border-gray-300 px-4 py-2">{currentDate.toDateString()}</td>
                    <td className="border border-gray-300 px-4 py-2">
                      {attendanceRecord ? (
                        attendanceRecord.AttendanceStatus.isPresent ? "Present" :
                        attendanceRecord.AttendanceStatus.isLeave ? "Leave" :
                        attendanceRecord.AttendanceStatus.isHalfDay ? "Half Day" : "Absent"
                      ) : "No Data"}
                    </td>
                    <td className="border border-gray-300 px-4 py-2">
                      {attendanceRecord ? attendanceRecord.location.locationName : ""}
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      ) : (
        <p className="text-center text-gray-500">Employee not found.</p>
      )}
    </div>
  );
};

export default AttendanceTable;
