import React, { useState, useEffect } from "react";

const UserRequest = ({ employeeId }) => {
  const [formData, setFormData] = useState({
    employeeId: "",
    name: "",
    mobileNumber: "",
    alternateMobileNumber: "",
    password: "",
    confirmPassword: "",
    branch: "",
    designation: "",
    dateOfBirth: "",
    address: "",
    joinDate: "",
    fatherName: "",
    spouseName: "",
    salary: "",
    gender: "",
    createdAt: "",
    updatedAt: "",
    __v: 0,
  });

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/users/${employeeId}`);
        if (!response.ok) {
          throw new Error("Data not found");
        }
        const data = await response.json();
        setFormData(data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchData();
  }, [employeeId]);

  if (loading) return <p className="text-center text-blue-500">Loading...</p>;
  if (error) return <p className="text-center text-red-500">Error: {error}</p>;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Form Data Submitted:", formData);
  };

  return (
    <div className="max-w-xl mx-auto p-6 bg-gradient-to-br from-purple-500 to-indigo-500 text-white rounded-lg shadow-lg">
      <h2 className="text-3xl font-bold text-center mb-6">User Request Form</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        
        {/* Employee ID */}
        <div>
          <label className="block font-medium mb-1">Employee ID</label>
          <input
            type="text"
            name="employeeId"
            value={formData.employeeId}
            onChange={handleChange}
            className="w-full p-3 rounded bg-white text-gray-800 shadow-inner focus:outline-none"
            readOnly
          />
        </div>

        {/* Name */}
        <div>
          <label className="block font-medium mb-1">Name</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            className="w-full p-3 rounded bg-white text-gray-800 shadow-inner focus:outline-none"
          />
        </div>

        {/* Mobile Number */}
        <div>
          <label className="block font-medium mb-1">Mobile Number</label>
          <input
            type="text"
            name="mobileNumber"
            value={formData.mobileNumber}
            onChange={handleChange}
            className="w-full p-3 rounded bg-white text-gray-800 shadow-inner focus:outline-none"
          />
        </div>

        {/* Additional fields can follow the same pattern */}

        {/* Submit Button */}
        <button
          type="submit"
          className="w-full p-3 bg-gradient-to-r from-green-400 to-blue-500 rounded-md shadow-lg text-white font-semibold text-lg hover:from-green-500 hover:to-blue-600 transition-all duration-300 ease-in-out transform hover:scale-105"
        >
          Submit
        </button>
      </form>
    </div>
  );
};

export default UserRequest;
