import React, { useEffect, useState } from 'react';
import axios from 'axios';

const MessagesCard = () => {
  const [messages, setMessages] = useState([]);
  const [filteredMessages, setFilteredMessages] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');

  // Fetch messages from backend API
  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await axios.get('http://localhost:3005/getmessages');
        setMessages(response.data);
        setFilteredMessages(response.data); // Set initially to all messages
      } catch (error) {
        console.error('Error fetching messages:', error);
      }
    };

    fetchMessages();
  }, []);

  // Handle search input
app.get('/search-messages', async (req, res) => {
  const query = req.query.query; // Get search query from the request query params

  if (!query) {
    return res.status(400).json({ error: 'Search query is required' });
  }

  try {
    // Use regex to find matching messages (case-insensitive search)
    const messages = await Message.find({
      $or: [
        { phoneNumber: { $regex: query, $options: 'i' } },  // Search by phone number
        { messageContent: { $regex: query, $options: 'i' } } // Search by message content
      ]
    });

    if (messages.length === 0) {
      return res.status(404).json({ message: 'No matching messages found' });
    }

    res.status(200).json(messages); // Return matching messages
  } catch (error) {
    console.error('Error searching messages:', error);
    res.status(500).json({ error: 'Error searching messages' });
  }
});

  

  // Handle delete message
  const handleDeleteMessage = async (id) => {
    const confirmDelete = window.confirm('Do you really want to delete this message?');
    if (confirmDelete) {
      try {
        await axios.delete(`http://localhost:3005/messages/${id}`);
        setFilteredMessages(filteredMessages.filter((msg) => msg._id !== id)); // Remove the deleted message from the UI
      } catch (error) {
        console.error('Error deleting message:', error);
      }
    }
  };

  // Handle delete all messages
  const handleDeleteAllMessages = async () => {
    const confirmDeleteAll = window.confirm('Do you really want to delete all messages?');
    if (confirmDeleteAll) {
      try {
        await axios.delete('http://localhost:3005/messages');
        setFilteredMessages([]); // Clear the UI after deletion
      } catch (error) {
        console.error('Error deleting all messages:', error);
      }
    }
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Messages</h2>

      {/* Search bar */}
      <input
        type="text"
        placeholder="Search messages..."
        value={searchQuery}
        onChange={handleSearch}
        className="transition rounded-sm mt-4 border border-blue-700 p-3 w-60 mb-4"
      />

      {/* Delete All Messages Button */}
      <button
        onClick={handleDeleteAllMessages}
        className="bg-red-500 text-white px-4 py-2 rounded-lg mb-4 ml-4"
      >
        Delete All Messages
      </button>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {filteredMessages.length > 0 ? (
          filteredMessages.map((msg) => (
            <div
              key={msg._id} // Assuming the MongoDB ID is used
              className="bg-white p-4 rounded-lg shadow-md flex flex-col justify-between"
            >
              <div className="mb-4">
                <p className="text-gray-600 font-semibold">From: {msg.phoneNumber}</p>
                <p className="text-gray-800 mt-2">{msg.messageContent}</p> {/* Use correct message field */}
              </div>
              <div className="text-gray-500 text-sm">
                <p>Received: {new Date(msg.timestamp).toLocaleDateString()}</p>
                <p>Time: {new Date(msg.timestamp).toLocaleTimeString()}</p>
              </div>

              {/* Delete Message Button */}
              <button
                onClick={() => handleDeleteMessage(msg._id)}
                className="bg-red-500 text-white px-2 py-1 rounded-lg mt-4"
              >
                Delete
              </button>
            </div>
          ))
        ) : (
          <p>No messages available</p>
        )}
      </div>
    </div>
  );
};

export default MessagesCard;
