import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { BASE_URL } from './constants';

const LeaveRequest = () => {
  const [leaveRequests, setLeaveRequests] = useState(() => {
    // Load leave requests from local storage initially
    const savedLeaveRequests = localStorage.getItem('leaveRequests');
    return savedLeaveRequests ? JSON.parse(savedLeaveRequests) : [];
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  const fetchLeaveRequests = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(`${BASE_URL}/leaves`);
      const fetchedLeaveRequests = response.data;
      setLeaveRequests(fetchedLeaveRequests);
      // Save fetched leave requests to local storage
      localStorage.setItem('leaveRequests', JSON.stringify(fetchedLeaveRequests));
    } catch (err) {
      setError('Error fetching leave requests. Please try again later.');
      console.error('Error fetching leave requests:', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (!leaveRequests.length) {
      fetchLeaveRequests();
    } else {
      setLoading(false);
    }
  }, []);

  const retryFetch = () => {
    fetchLeaveRequests();
  };

  const goToLeaveDetails = (leaveId) => {
    navigate(`/leaves/${leaveId}/details`);
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const filteredLeaveRequests = leaveRequests.filter(
    (leaveRequest) =>
      leaveRequest.employeeName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      leaveRequest.employeeId?.toString().includes(searchTerm)
  );

const handleLeaveStatusChange = async (leaveId, status) => {
    try {
      const response = await axios.post(`${BASE_URL}/leaves/${leaveId}/status`, { status });
      const updatedLeaveRequest = response.data;

      const updatedLeaveRequests = leaveRequests.map((request) =>
        request._id === updatedLeaveRequest._id // Assuming _id is the unique identifier
          ? { ...request, approveStatus: updatedLeaveRequest.approveStatus } // Correct field name
          : request
      );

      setLeaveRequests(updatedLeaveRequests);
      // Update local storage with the new data
      localStorage.setItem('leaveRequests', JSON.stringify(updatedLeaveRequests));
    } catch (err) {
      console.error(`Error changing status for leave request ${leaveId}:`, err);
      setError('Error changing leave status. Please try again.');
    }
};


  const LeaveRequestRow = ({ leaveRequest, index }) => (
    <tr key={leaveRequest.leaveId} className="hover:bg-gray-100 transition-colors duration-200">
      <td className="border border-gray-300 px-4 py-2 text-center">{index + 1}</td>
      <td className="border border-gray-300 px-4 py-2">{leaveRequest.employeeId}</td>
      <td className="border border-gray-300 px-4 py-2">{leaveRequest.employeeName}</td>
      <td className="border border-gray-300 px-4 py-2">{leaveRequest.leaveType}</td>
      <td className="border border-gray-300 px-4 py-2">{leaveRequest.leaveDate}</td>
      <td className="border border-gray-300 px-4 py-2">{leaveRequest.leaveReason}</td>
      <td className="border border-gray-300 px-4 py-2">
        <button onClick={() => goToLeaveDetails(leaveRequest.leaveId)} className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-1 px-2 rounded mr-2">
          View
        </button>
      </td>
      <td className="border border-gray-300 px-4 py-2 gap-3 flex items-center justify-center">
        {leaveRequest.status === 'Approved' ? (
          <span className="text-green-500 font-bold">Approved</span>
        ) : leaveRequest.status === 'Rejected' ? (
          <span className="text-red-500 font-bold">Rejected</span>
        ) : (
          <>
            <button onClick={() => handleLeaveStatusChange(leaveRequest.leaveId, 'Approved')} className="bg-green-500 hover:bg-green-700 text-white font-bold py-1 px-2 rounded mr-2">
              Approve
            </button>
            <button onClick={() => handleLeaveStatusChange(leaveRequest.leaveId, 'Rejected')} className="bg-red-500 hover:bg-red-700 text-white font-bold py-1 px-2 rounded mr-2">
              Reject
            </button>
          </>
        )}
      </td>
    </tr>
  );

  return (
    <div className="p-4 w-full max-w-5xl mx-auto mt-8 absolute left-[300px]">
      <h2 className="text-3xl font-bold mb-6 text-center">Leave Requests</h2>

      <input
        type="text"
        placeholder="Search by Employee ID, Name, or Leave Type"
        value={searchTerm}
        onChange={handleSearch}
        className="w-full mb-4 p-2 border border-gray-300 rounded"
      />

      {loading ? (
        <div className="text-center">
          <p className="text-gray-500">Loading leave requests...</p>
        </div>
      ) : error ? (
        <div className="text-center text-red-500">
          <p>{error}</p>
          <button onClick={retryFetch} className="mt-4 bg-red-500 text-white px-4 py-2 rounded hover:bg-red-700">
            Retry
          </button>
        </div>
      ) : filteredLeaveRequests.length > 0 ? (
        <div className="overflow-x-auto">
          <table className="w-full bg-white border border-gray-300 rounded-lg shadow-md">
            <thead className="bg-gray-100">
              <tr>
                <th className="border border-gray-300 px-4 py-3 text-left text-sm font-medium text-gray-600">SL No</th>
                <th className="border border-gray-300 px-4 py-3 text-left text-sm font-medium text-gray-600">Employee ID</th>
                <th className="border border-gray-300 px-4 py-3 text-left text-sm font-medium text-gray-600">Employee Name</th>
                <th className="border border-gray-300 px-4 py-3 text-left text-sm font-medium text-gray-600">Leave Type</th>
                <th className="border border-gray-300 px-4 py-3 text-left text-sm font-medium text-gray-600">Leave Date</th>
                <th className="border border-gray-300 px-4 py-3 text-left text-sm font-medium text-gray-600">Leave Reason</th>
                <th className="border border-gray-300 px-4 py-3 text-left text-sm font-medium text-gray-600">Details</th>
                <th className="border border-gray-300 px-4 py-3 text-left text-sm font-medium text-gray-600">Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredLeaveRequests.map((leaveRequest, index) => (
                <LeaveRequestRow key={leaveRequest.leaveId} leaveRequest={leaveRequest} index={index} />
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <div className="text-center">
          <p className="text-gray-500">No leave requests available.</p>
        </div>
      )}
    </div>
  );
};

export default LeaveRequest;
