import React, { useEffect, useState } from 'react';
import axios from 'axios';

const MessagesCard = () => {
  const [messages, setMessages] = useState([]); // State to hold messages data
  const [searchTerm, setSearchTerm] = useState(''); // State for the search term
  const [filteredMessages, setFilteredMessages] = useState([]); // State for filtered messages

  // Fetch messages from backend API
  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await axios.get('http://localhost:3005/getmessages'); // Replace with your API endpoint
        setMessages(response.data); // Assuming the response contains an array of messages
        setFilteredMessages(response.data); // Initialize filtered messages
      } catch (error) {
        console.error('Error fetching messages:', error);
      }
    };

    fetchMessages();
  }, []);

  // Filter messages based on search term
  useEffect(() => {
    const filtered = messages.filter((msg) => {
      const phoneNumber = msg.phoneNumber || ''; // Default to empty string if undefined
      const messageContent = msg.messageContent || ''; // Default to empty string if undefined
      
      return (
        phoneNumber.toLowerCase().includes(searchTerm.toLowerCase()) ||
        messageContent.toLowerCase().includes(searchTerm.toLowerCase())
      );
    });
    setFilteredMessages(filtered);
  }, [searchTerm, messages]);

  // Handle search input change
  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value); // Update the search term
  };

  // Handle delete a single message with confirmation
  const handleDelete = async (id) => {
    if (window.confirm('Do you really want to delete this message?')) { // Confirmation dialog
      try {
        await axios.delete(`http://localhost:3005/delete-message/${id}`); // Call delete endpoint
        setMessages(messages.filter(msg => msg._id !== id)); // Update state to remove the deleted message
        setFilteredMessages(filteredMessages.filter(msg => msg._id !== id)); // Update filtered messages
      } catch (error) {
        console.error('Error deleting message:', error);
      }
    }
  };

  // Handle delete all messages
  const handleDeleteAll = async () => {
    if (window.confirm('Do you really want to delete all messages?')) { // Confirm before deletion
      try {
        await axios.delete('http://localhost:3005/delete-all-messages'); // Call delete all endpoint
        setMessages([]); // Clear messages from state
        setFilteredMessages([]); // Clear filtered messages
      } catch (error) {
        console.error('Error deleting all messages:', error);
      }
    }
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Messages</h2>
      <input
        type="text"
        placeholder="Search messages..."
        value={searchTerm}
        onChange={handleSearchChange}
        className="border p-2 rounded mb-4"
      />
      <button
        onClick={handleDeleteAll}
        className="bg-red-500 text-white px-4 py-2 rounded mb-4"
      >
        Delete All Messages
      </button>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {filteredMessages.length > 0 ? (
          filteredMessages.map((msg) => (
            <div
              key={msg._id}  // Assuming the MongoDB ID is used
              className="bg-white p-4 rounded-lg shadow-md flex flex-col justify-between"
            >
              <div className="mb-4">
                <p className="text-gray-600 font-semibold">From: {msg.phoneNumber}</p>
                <p className="text-gray-800 mt-2">{msg.messageContent}</p> {/* Use correct message field */}
              </div>
              <div className="text-gray-500 text-sm">
                <p>Received: {new Date(msg.timestamp).toLocaleDateString()}</p>
                <p>Time: {new Date(msg.timestamp).toLocaleTimeString()}</p>
              </div>
              <button
                onClick={() => handleDelete(msg._id)} // Call handleDelete with confirmation
                className="mt-4 bg-red-500 text-white px-4 py-2 rounded"
              >
                Delete
              </button>
            </div>
          ))
        ) : (
          <p>No messages available</p>
        )}
      </div>
    </div>
  );
};

export default MessagesCard;
