import React, { useState, useEffect } from 'react';
import axios from 'axios';

// Sample complaints data
const dummyComplaints = [
  {
    from: 'John Doe',
    to: 'Manager',
    subject: 'Delay in Project Delivery',
    message: 'The project delivery has been delayed by two weeks due to unforeseen circumstances.',
  },
  {
    from: 'Jane Smith',
    to: 'HR',
    subject: 'Leave Request',
    message: 'I would like to request a leave for two days due to personal reasons.',
  },
  {
    from: 'Mike Johnson',
    to: 'IT Support',
    subject: 'System Issue',
    message: 'My system has been crashing frequently, and I need immediate assistance.',
  },
];

const ComplaintPage = () => {
  const [employee, setEmployee] = useState({}); // Store employee data
  const [complaints, setComplaints] = useState([]); // Store complaints data
  const employeeId = 'E123'; // Replace with dynamic employee ID as needed

  // Fetch employee data from API (mocking for now)
  useEffect(() => {
    const fetchEmployeeData = async () => {
      try {
        // Replace with actual API call to get employee details
        const employeeResponse = await axios.get(`http://localhost:3005/employees/${employeeId}`);
        setEmployee(employeeResponse.data);
        
        // Set dummy complaints data for now
        setComplaints(dummyComplaints);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchEmployeeData();
  }, [employeeId]);

  return (
    <div className="p-6 bg-gray-100 absolute top-20 right-0 left-[275px]">
      {/* Employee Information */}
      <div className="bg-gradient-to-r from-green-300 to-blue-300 p-4 rounded-lg shadow-lg mb-6">
        <h2 className="text-2xl font-bold text-white">Employee Information</h2>
        <p className="text-lg text-white">ID: {employee.employeeId || employeeId}</p>
        <p className="text-lg text-white">Name: {employee.name || 'N/A'}</p>
        <p className="text-lg text-white">Number: {employee.mobileNumber || 'N/A'}</p>
      </div>

      {/* Complaints List */}
      <h2 className="text-2xl font-bold mb-4 text-blue-600">Complaints</h2>
      <div className="space-y-4">
        {complaints.length > 0 ? (
          complaints.map((complaint, index) => (
            <div key={index} className="bg-white p-4 rounded-lg shadow-lg border-l-4 border-purple-500">
              <h3 className="text-xl font-semibold text-purple-600">{complaint.subject}</h3>
              <p className="text-gray-600">From: <span className="font-semibold">{complaint.from}</span></p>
              <p className="text-gray-600">To: <span className="font-semibold">{complaint.to}</span></p>
              <p className="text-gray-700">{complaint.message}</p>
            </div>
          ))
        ) : (
          <p>No complaints available.</p>
        )}
      </div>
    </div>
  );
};

export default ComplaintPage;
