import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ComplaintPage = () => {
  const [employee, setEmployee] = useState({});
  const [complaints, setComplaints] = useState([]);
  const employeeId = 'E123'; // Replace with dynamic employee ID as needed

  // Dummy complaints data
  const dummyComplaints = [
    {
      from: 'Alice Johnson',
      to: 'HR',
      subject: 'Workplace Safety Concerns',
      message: 'I have noticed several safety issues in the workplace that need to be addressed.',
    },
    {
      from: 'Bob Smith',
      to: 'Manager',
      subject: 'Request for Equipment',
      message: 'I need additional equipment to complete my tasks efficiently.',
    },
    {
      from: 'Charlie Brown',
      to: 'IT Support',
      subject: 'Access to Software',
      message: 'I am unable to access the new software that was implemented last week.',
    },
  ];

  useEffect(() => {
    // Fetch employee data from API (mocking for now)
    const fetchEmployeeData = async () => {
      try {
        const employeeResponse = await axios.get(`http://localhost:3005/employees/${employeeId}/attendance-list/complaints`);
        setEmployee(employeeResponse.data);

        // Commenting out API call for complaints to use dummy data
         const complaintsResponse = await axios.get(`http://localhost:3005/complaints/${employeeId}`);
         setComplaints(complaintsResponse.data);
        
        // Using dummy complaints
        setComplaints(dummyComplaints);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchEmployeeData();
  }, [employeeId]);

  // Debugging log to check complaints
  console.log('Complaints:', complaints);

  return (
    <div className="p-6 bg-gray-100 absolute top-20 right-0 left-[275px]">
      {/* Employee Information */}
      <div className="bg-gradient-to-r from-teal-400 to-cyan-400 p-4 rounded-lg shadow-lg mb-6">
        <h2 className="text-2xl font-bold text-white">Employee Information</h2>
        <p className="text-lg text-white">ID: {employee.employeeId || employeeId}</p>
        <p className="text-lg text-white">Name: {employee.name || 'N/A'}</p>
        <p className="text-lg text-white">Number: {employee.mobileNumber || 'N/A'}</p>
      </div>

      {/* Complaints List */}
      <h2 className="text-2xl font-bold mb-4 text-purple-700">Complaints</h2>
      <div className="space-y-4">
        {complaints.length > 0 ? (
          complaints.map((complaint, index) => (
            <div key={index} className="bg-white p-4 rounded-lg shadow-lg border-l-4 border-orange-500">
              <h3 className="text-xl font-semibold text-orange-600">{complaint.subject}</h3>
              <p className="text-gray-600">From: <span className="font-semibold">{complaint.from}</span></p>
              <p className="text-gray-600">To: <span className="font-semibold">{complaint.to}</span></p>
              <p className="text-gray-700">{complaint.message}</p>
            </div>
          ))
        ) : (
          <p className="text-gray-600">No complaints to display.</p>
        )}
      </div>
    </div>
  );
};

export default ComplaintPage;
