import React, { useState, useEffect } from "react";
import axios from "axios";
import { FaPhone, FaEnvelope, FaMapMarkerAlt, FaCheckCircle } from "react-icons/fa";
import { Link, useParams } from "react-router-dom";
import Sidebar from "./Sidebar";

const Dashboard = () => {
  const { employeeId } = useParams(); // Get employeeId from URL parameters
  const [totalCalls, setTotalCalls] = useState(0);
  const [totalMessages, setTotalMessages] = useState(0);
  const [attendanceStatus, setAttendanceStatus] = useState("N/A"); // New state for attendance status
  const [employeeData, setEmployeeData] = useState(""); // State for employee data
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Function to fetch employee data
  const fetchEmployeeData = async () => {
    setLoading(true);
    setError(null);

    try {
      const employeeResponse = await axios.get(`http://localhost:3005/employees/${employeeId}/`); // Fetch employee data

      // Updating state with the fetched data
      setEmployeeData(employeeResponse.data); // Correctly access employee data from the response

      const callResponse = await axios.get(`http://localhost:3005/employees/${employeeId}/call-logs`);
      const messageResponse = await axios.get(`http://localhost:3005/employees/${employeeId}/messages`);
      const attendanceResponse = await axios.get(`http://localhost:3005/employees/${employeeId}/attendance-list`);

      setTotalCalls(callResponse.data.totalCalls || 0);
      setTotalMessages(messageResponse.data.totalMessages || 0);
      setAttendanceStatus(attendanceResponse.data.status || "N/A");
      setError(null); // Reset error state if successful
    } catch (err) {
      setError("Error fetching employee data. Please check the Employee ID.");
      console.error("Error fetching employee data:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (employeeId) {
      fetchEmployeeData(); // Fetch data when Employee ID is available from URL
    }
  }, [employeeId]); // Trigger API call when employeeId changes

  return (
    <div className="p-4 absolute top-16 left-[270px] right-0">
      {/* Error message */}
      {error && <p className="text-red-500 text-center mb-4">{error}</p>}

      {/* Employee Info Card */}
      <div className="bg-gray-300 shadow-lg hover:shadow-xl transition-shadow shadow-violet-700 rounded-lg p-6 mb-6 flex flex-col">
        <h2 className="text-3xl font-bold mb-2">Employee Info</h2>
        <p className="text-gray-700 text-xl">Name: {employeeData.name || "N/A"}</p>
        <p className="text-gray-700 text-xl">Employee ID: {employeeData.employeeId || "N/A"}</p>
        <p className="text-gray-700 text-xl">Phone Number: {employeeData.mobileNumber || "N/A"}</p>
        <p className="text-gray-700 text-xl">Salary: {employeeData.salary || "N/A"}</p>
      </div>

      {/* Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Total Calls Card */}
        <Link to="/call-Details">
          <div className="bg-gray-300 shadow-md rounded-lg p-6 flex items-center justify-between hover:shadow-xl transition-shadow shadow-blue-600 translate-x-0 duration-100">
            <div>
              <h2 className="text-2xl font-bold">Total Calls</h2>
              <p className="text-gray-800 mt-1">{totalCalls} calls</p>
            </div>
            <FaPhone className="text-blue-500 text-3xl" />
          </div>
        </Link>

        {/* Total Messages Card */}
        <Link to="/message-Details">
          <div className="bg-gray-300 shadow-lg rounded-lg p-6 flex items-center justify-between hover:shadow-xl shadow-green-500 transition-shadow">
            <div>
              <h2 className="text-2xl font-bold">Messages</h2>
              <p className="text-gray-800 mt-1">{totalMessages} messages</p>
            </div>
            <FaEnvelope className="text-green-500 text-3xl" />
          </div>
        </Link>

        {/* Attendance Status Card */}
        <div className="bg-gray-300 shadow-lg rounded-lg p-6 flex items-center justify-between shadow-yellow-300 hover:shadow-xl transition-shadow">
          <div>
            <h2 className="text-2xl font-bold">Attendance Status</h2>
            <p className="text-gray-800 mt-1">{attendanceStatus}</p>
          </div>
          <FaCheckCircle className="text-green-500 text-3xl" />
        </div>

        {/* Location Updates Card */}
        <div className="bg-gray-300 shadow-lg rounded-lg p-6 flex items-center justify-between shadow-red-500 hover:shadow-xl transition-shadow">
          <div>
            <h2 className="text-2xl font-bold">Location Updates</h2>
            <p className="text-gray-800 mt-1">50 updates</p> {/* Hardcoded for now */}
          </div>
          <FaMapMarkerAlt className="text-red-500 text-3xl" />
        </div>
      </div>

      {/* Loading Indicator */}
      {loading && <p className="text-center text-blue-500 mt-4">Fetching data...</p>}
    </div>
  );
};

export default Dashboard;
