import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, Link } from "react-router-dom";
import Calendar from 'react-calendar'; // Import the Calendar component
import 'react-calendar/dist/Calendar.css'; // Import the CSS for the calendar

const AttendanceDetails = () => {
  const [employee, setEmployee] = useState(null); // Single employee
  const [attendanceData, setAttendanceData] = useState([]); // Store attendance records
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { employeeId } = useParams(); // Get the employeeId from the URL
  const [selectedDate, setSelectedDate] = useState(new Date()); // State for selected date

  // Fetch employee and attendance data when the employeeId changes
  useEffect(() => {
    const fetchEmployeeData = async () => {
      try {
        const response = await axios.get(`http://localhost:3005/employees/${employeeId}`);
        setEmployee(response.data);
      } catch (err) {
        setError("Error fetching employee data. Please try again later.");
      }
    };

    const fetchAttendanceData = async () => {
      try {
        const response = await axios.get(`http://localhost:3005/employees/${employeeId}/attendance-list`);
        setAttendanceData(response.data);
      } catch (err) {
        setError("Error fetching attendance data. Please try again later.");
      } finally {
        setLoading(false);
      }
    };

    fetchEmployeeData();
    fetchAttendanceData();
  }, [employeeId]);

  // Handle selected date change
  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  // Render the status of the employee for a selected date
  const renderAttendanceStatus = (date) => {
    const record = attendanceData.find(att => new Date(att.date).toDateString() === date.toDateString());
    if (record) {
      return record.status === "Present" ? (
        <span className="bg-green-100 text-green-600 px-3 py-1 rounded-full text-sm font-bold">Present</span>
      ) : (
        <span className="bg-red-100 text-red-600 px-3 py-1 rounded-full text-sm font-bold">Absent</span>
      );
    }
    return null;
  };

  return (
    <div className="p-6 max-w-7xl mx-auto bg-gray-100 rounded-lg shadow-lg absolute top-20 right-10 left-[275px]">
      <h2 className="text-4xl font-bold mb-6 text-center text-blue-600">Employee Attendance</h2>

      {/* Calendar Component */}
      <div className="mb-6">
        <Calendar 
          onChange={handleDateChange} 
          value={selectedDate} 
          className="mx-auto" 
        />
        <p className="text-center text-gray-600 mt-2">Selected Date: {selectedDate.toDateString()}</p>
      </div>

      {loading ? (
        <p className="text-center text-gray-500">Loading attendance...</p>
      ) : error ? (
        <p className="text-center text-red-500">{error}</p>
      ) : employee ? (
        <div className="mb-8">
          <div className="mt-4 mb-4 text-right">
            <Link to='./leaves'>
              <button className="bg-red-500 mr-4 hover:bg-red-700 text-white font-bold py-2 px-4 rounded shadow-lg">Leave Requests</button>
            </Link>
            <Link to='./complaints'>
              <button className="bg-yellow-500 hover:bg-yellow-700 text-white font-bold py-2 px-4 rounded">Complaints</button>
            </Link>
          </div>

          {/* Employee Details */}
          <div className="bg-blue-500 text-white p-4 rounded-lg">
            <h3 className="text-2xl font-semibold">Employee Details</h3>
            <p><strong>ID:</strong> {employee.employeeId}</p>
            <p><strong>Name:</strong> {employee.name}</p>
            <p><strong>Mobile Number:</strong> {employee.mobileNumber}</p>
            <p><strong>Selected Date:</strong> {selectedDate.toDateString()}</p>
          </div>

          {/* Attendance Table */}
          <table className="min-w-full bg-white mt-4 border border-gray-300 rounded-lg shadow-md">
            <thead className="bg-gray-200">
              <tr>
                <th className="border border-gray-300 px-4 py-2 text-left text-sm font-medium text-gray-600">Date</th>
                <th className="border border-gray-300 px-4 py-2 text-left text-sm font-medium text-gray-600">Status</th>
                <th className="border border-gray-300 px-4 py-2 text-left text-sm font-medium text-gray-600">Reason for Leave</th>
              </tr>
            </thead>
            <tbody>
              {attendanceData.map((record) => (
                <tr key={record.date} className="hover:bg-gray-100 transition-colors duration-200">
                  <td className="border border-gray-300 px-4 py-2">{new Date(record.date).toDateString()}</td>
                  <td className="border border-gray-300 px-4 py-2">
                    {renderAttendanceStatus(new Date(record.date))}
                  </td>
                  <td className="border border-gray-300 px-4 py-2">
                    {record.status === "Absent" && record.reason}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <p className="text-center text-gray-500">Employee not found.</p>
      )}
    </div>
  );
};

export default AttendanceDetails;
