import React, { useEffect, useState } from "react";
import axios from "axios";
import { FaCircle, FaChevronDown, FaChevronUp } from "react-icons/fa"; // Import icons for toggle
import { Collapse } from "react-collapse"; // To handle collapsible sections

const Employee = () => {
  const [employees, setEmployees] = useState([]); // State to hold employees data
  const [expandedEmployee, setExpandedEmployee] = useState(null); // Track expanded employee for call logs and messages

  // Fetch employees from backend API
  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const response = await axios.get("http://localhost:3005/employees"); // Replace with your API endpoint
        setEmployees(response.data); // Assuming the response contains an array of employees
      } catch (error) {
        console.error("Error fetching employees:", error);
      }
    };

    fetchEmployees();
  }, []);

  // Toggle the collapse/expand of call logs and messages for a specific employee
  const toggleExpand = (employeeId) => {
    setExpandedEmployee(expandedEmployee === employeeId ? null : employeeId);
  };

  return (
    <div className="p-4 max-w-7xl mx-auto absolute top-16 left-[270px] right-0">
      <h2 className="text-3xl font-bold mb-6 text-center">Employee List</h2>
      {employees.length > 0 ? (
        <table className="min-w-full bg-white border border-gray-300 rounded-lg shadow-md">
          <thead className="bg-gray-100">
            <tr>
              <th className="border border-gray-300 px-6 py-3 text-left text-sm font-medium text-gray-600">SL No</th>
              <th className="border border-gray-300 px-6 py-3 text-left text-sm font-medium text-gray-600">Employee ID</th>
              <th className="border border-gray-300 px-6 py-3 text-left text-sm font-medium text-gray-600">Employee Name</th>
              <th className="border border-gray-300 px-6 py-3 text-left text-sm font-medium text-gray-600">Mobile Number</th>
              <th className="border border-gray-300 px-6 py-3 text-left text-sm font-medium text-gray-600">Details</th>
              <th className="border border-gray-300 px-6 py-3 text-left text-sm font-medium text-gray-600">Active Status</th>
            </tr>
          </thead>
          <tbody>
            {employees.map((employee, index) => (
              <React.Fragment key={employee._id}>
                <tr className="hover:bg-gray-100 transition-colors duration-200">
                  <td className="border border-gray-300 px-6 py-4">{index + 1}</td>
                  <td className="border border-gray-300 px-6 py-4">{employee.employeeId}</td>
                  <td className="border border-gray-300 px-6 py-4">{employee.name}</td>
                  <td className="border border-gray-300 px-6 py-4">{employee.mobileNumber}</td>
                  <td className="border border-gray-300 px-6 py-4 text-center">
                    {/* Toggle Button */}
                    <button
                      onClick={() => toggleExpand(employee._id)}
                      className="text-blue-500 focus:outline-none"
                    >
                      {expandedEmployee === employee._id ? (
                        <>
                          Hide Details <FaChevronUp className="inline" />
                        </>
                      ) : (
                        <>
                          Show Details <FaChevronDown className="inline" />
                        </>
                      )}
                    </button>
                  </td>
                  <td className="border border-gray-300 px-6 py-4 flex items-center">
                    {employee.isActive ? (
                      <>
                        <FaCircle className="text-green-500 mr-2" />
                        <span>Online</span>
                      </>
                    ) : (
                      <>
                        <FaCircle className="text-red-500 mr-2" />
                        <span>Offline</span>
                      </>
                    )}
                  </td>
                </tr>

                {/* Collapsible Section for Call Logs and Messages */}
                <tr>
                  <td colSpan="6" className="px-6 py-4">
                    <Collapse isOpened={expandedEmployee === employee._id}>
                      <div className="bg-gray-100 p-4 rounded-md shadow-md">
                        <h4 className="font-bold mb-2">Call Logs:</h4>
                        {employee.callLogs && employee.callLogs.length > 0 ? (
                          <ul className="list-disc ml-4 mb-4">
                            {employee.callLogs.map((callLog, idx) => (
                              <li key={idx}>
                                <strong>Type:</strong> {callLog.type}, <strong>Duration:</strong>{" "}
                                {callLog.duration}, <strong>Date:</strong> {new Date(callLog.dateTime).toLocaleString()}
                              </li>
                            ))}
                          </ul>
                        ) : (
                          <p>No call logs available</p>
                        )}

                        <h4 className="font-bold mb-2">Messages:</h4>
                        {employee.messages && employee.messages.length > 0 ? (
                          <ul className="list-disc ml-4">
                            {employee.messages.map((message, idx) => (
                              <li key={idx}>
                                <strong>Message:</strong> {message.text}, <strong>Sent At:</strong>{" "}
                                {new Date(message.timestamp).toLocaleString()}
                              </li>
                            ))}
                          </ul>
                        ) : (
                          <p>No messages available</p>
                        )}
                      </div>
                    </Collapse>
                  </td>
                </tr>
              </React.Fragment>
            ))}
          </tbody>
        </table>
      ) : (
        <p className="text-center text-gray-500">No employees available</p>
      )}
    </div>
  );
};

export default Employee;
