import React from 'react';

const AttendanceTable = () => {
  // Array of dates for the attendance table
  const dates = Array.from({ length: 31 }, (_, i) => i + 1); // Dates from 1 to 31
  // Sample empty attendance data
  const attendanceData = dates.map((date) => ({
    date,
    employeeId: '',
    employeeName: '',
    mobileNumber: '',
    status: '', // 'Present' or 'Absent'
  }));

  return (
    <div className="p-4 max-w-7xl mx-auto mt-10 absolute left-[270px] right-0">
      <h2 className="text-4xl font-bold mb-6 text-center text-blue-600">Attendance List</h2>
      <table className="min-w-full bg-gray-50 border border-gray-200 rounded-lg shadow-lg">
        <thead className="bg-blue-200">
          <tr>
            <th className="border border-gray-200 px-4 py-3 text-left text-sm font-medium text-gray-800">Date</th>
            <th className="border border-gray-200 px-4 py-3 text-left text-sm font-medium text-gray-800">Employee ID</th>
            <th className="border border-gray-200 px-4 py-3 text-left text-sm font-medium text-gray-800">Employee Name</th>
            <th className="border border-gray-200 px-4 py-3 text-left text-sm font-medium text-gray-800">Mobile Number</th>
            <th className="border border-gray-200 px-4 py-3 text-left text-sm font-medium text-gray-800">Attendance Status</th>
          </tr>
        </thead>
        <tbody>
          {attendanceData.map((record, index) => (
            <tr key={index} className="hover:bg-blue-100 transition-colors duration-200">
              <td className="border border-gray-200 px-4 py-4 text-gray-700">{record.date}</td>
              <td className="border border-gray-200 px-4 py-4 text-gray-700">{record.employeeId || '-'}</td>
              <td className="border border-gray-200 px-4 py-4 text-gray-700">{record.employeeName || '-'}</td>
              <td className="border border-gray-200 px-4 py-4 text-gray-700">{record.mobileNumber || '-'}</td>
              <td className="border border-gray-200 px-4 py-4">
                <span className={`font-bold ${record.status === 'Present' ? 'text-green-600' : 'text-red-600'}`}>
                  {record.status || '-'}
                </span>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AttendanceTable;
