import React, { useState, useEffect } from "react";
import axios from "axios";
import { FaPhone, FaEnvelope, FaMapMarkerAlt } from "react-icons/fa";
import { Link } from "react-router-dom";
import Sidebar from "./Sidebar";

const RecentActivityTable = ({ recentCalls }) => {
  return (
    <div className="mt-8 bg-gray-200 shadow-lg shadow-gray-500 rounded-lg p-6">
      <h2 className="text-xl font-bold mb-4">Recent Call Logs</h2>
      <table className="min-w-full table-auto">
        <thead>
          <tr>
            <th className="px-4 py-2 text-left text-gray-600">Caller</th>
            <th className="px-4 py-2 text-left text-gray-600">Time</th>
            <th className="px-4 py-2 text-left text-gray-600">Duration</th>
            <th className="px-4 py-2 text-left text-gray-600">Status</th>
          </tr>
        </thead>
        <tbody>
          {recentCalls.map((call, index) => (
            <tr key={index} className="hover:bg-blue-200">
              <td className="border px-4 py-2">{call.name || "N/A"}</td>
              <td className="border px-4 py-2">
                {new Date(call.dateTime).toLocaleTimeString()}
              </td>
              <td className="border px-4 py-2">{call.duration}</td>
              <td
                className={`border px-4 py-2 ${
                  call.status === "Completed" ? "text-green-500" : "text-red-500"
                }`}
              >
                {call.status}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const Dashboard = () => {
  const [messages, setMessages] = useState([]);
  const [recentCalls, setRecentCalls] = useState([]);
  const [callLogs, setCallLogs] = useState([]);
  const [totalCalls, setTotalCalls] = useState(0);
  const [totalMessages, setTotalMessages] = useState(0);

  // Fetch all call logs from the backend
  useEffect(() => {
    const fetchCallLogs = async () => {
      try {
        const response = await axios.get("/call-logs"); // Fetching all call logs
        
        if (response.status === 200) {
          setCallLogs(response.data);
          setRecentCalls(response.data.slice(0, 5)); // Set recent calls for table (last 5)
          setTotalCalls(response.data.length); // Total number of call logs
        }
      } catch (error) {
        console.error("Error fetching call logs:", error);
      }
    };

    const fetchMessages = async () => {
      try {
        const response = await axios.get("/messages"); // Fetching all messages
        
        if (response.status === 200) {
          setMessages(response.data);
          setTotalMessages(response.data.length); // Total number of messages
        }
      } catch (error) {
        console.error("Error fetching messages:", error);
      }
    };

    fetchCallLogs();
    fetchMessages();
  }, []);

  return (
    <div className="p-4 absolute top-16 left-[270px] right-0">
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {/* Cards */}
        <Link to="/call-log">
          <div className="bg-gray-300 shadow-md rounded-lg p-6 flex items-center justify-between hover:shadow-xl transition-shadow shadow-blue-600 translate-x-0 duration-100">
            <div>
              <h2 className="text-2xl font-bold">Total Calls</h2>
              <p className="text-gray-800 mt-1">{totalCalls} calls</p>
            </div>
            <FaPhone className="text-blue-500 text-3xl" />
          </div>
        </Link>
        <Link to="/messages">
          <div className="bg-gray-300 shadow-lg rounded-lg p-6 flex items-center justify-between hover:shadow-xl shadow-green-500 transition-shadow">
            <div>
              <h2 className="text-2xl font-bold">Messages</h2>
              <p className="text-gray-800 mt-1">{totalMessages} messages</p>
            </div>
            <FaEnvelope className="text-green-500 text-3xl" />
          </div>
        </Link>
        <div className="bg-gray-300 shadow-lg rounded-lg p-6 flex items-center justify-between shadow-red-500 hover:shadow-xl transition-shadow">
          <div>
            <h2 className="text-2xl font-bold">Location Updates</h2>
            <p className="text-gray-800 mt-1">50 updates</p>{" "}
            {/* Hardcoded for now */}
          </div>
          <FaMapMarkerAlt className="text-red-500 text-3xl" />
        </div>
      </div>

      {/* Recent Activity Table */}
      <RecentActivityTable recentCalls={recentCalls} />
    </div>
  );
};

export default Dashboard;
