import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const ComplaintPage = () => {
  const { employeeId } = useParams(); // Get the employeeId from the URL
  const [employee, setEmployee] = useState({});
  const [complaints, setComplaints] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        // Fetch employee details
        const employeeResponse = await axios.get(`http://localhost:3005/employees/${employeeId}/attendance-list/complaints`);
        setEmployee(employeeResponse.data);
        
        // Fetch complaints for the employee
        const complaintsResponse = await axios.get(`http://localhost:3005/complaints/${employeeId}`);
        setComplaints(complaintsResponse.data);
      } catch (err) {
        setError("Error fetching data. Please try again later.");
        console.error("Error fetching data:", err);
      } finally {
        setLoading(false);
      }
    };

  //   fetchData();
  // }, [employeeId]);


const fetchEmployeeComplaints = async () => {
    setLoading(true);
    try {
      // Fetch the attendance data for a single employee based on employeeId
      const response = await axios.get(`http://localhost:3005/employees/${employeeId}/attendance-list/complaints`);
      setEmployee(response.data);  // Set the data for the single employee
    } catch (err) {
      setError("Error fetching employee data. Please try again later.");
      console.error("Error fetching employee data:", err);
    } finally {
      setLoading(false);
    }
  };

  fetchEmployeeComplaints();
}, [employeeId]); 

  return (
    <div className="p-6 bg-gray-100 absolute top-20 right-0 left-[275px]">
      {/* Employee Information */}
      <div className="bg-gradient-to-r from-green-300 to-blue-300 p-4 rounded-lg shadow-lg mb-6">
        <h2 className="text-2xl font-bold text-white">Employee Information</h2>
        <p className="text-lg text-white">ID: {employee.employeeId || employeeId}</p>
        <p className="text-lg text-white">Name: {employee.name}</p>
        <p className="text-lg text-white">Number: {employee.mobileNumber}</p>
      </div>

      {/* Complaints List */}
      <h2 className="text-2xl font-bold mb-4 text-blue-600">Complaints</h2>
      <div className="space-y-4">
        {loading ? (
          <p className="text-center text-gray-500">Loading complaints...</p>
        ) : error ? (
          <p className="text-center text-red-500">{error}</p>
        ) : complaints.length > 0 ? (
          complaints.map((complaint, index) => (
            <div key={index} className="bg-white p-4 rounded-lg shadow-lg border-l-4 border-purple-500">
              <h3 className="text-xl font-semibold text-purple-600">{complaint.title}</h3>
              <p className="text-gray-600">From: <span className="font-semibold">{complaint.from}</span></p>
              <p className="text-gray-600">To: <span className="font-semibold">{complaint.to}</span></p>
              <p className="text-gray-700">{complaint.description}</p>
            </div>
          ))
        ) : (
          <p className="text-center text-gray-500">No complaints found.</p>
        )}
      </div>
    </div>
  );
};

export default ComplaintPage;
