import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

const CallLogTable = () => {
  const { employeeId } = useParams(); // Get employeeId from URL
  const [callLogs, setCallLogs] = useState([]);
  const [allCallLogs, setAllCallLogs] = useState([]); // Store all call logs
  const [searchQuery, setSearchQuery] = useState("");
  const [filterType, setFilterType] = useState("All"); // For filtering call type
  const [error, setError] = useState("");
  const navigate = useNavigate(); // Initialize navigate

  // Fetch call logs whenever employeeId changes
  useEffect(() => {
    if (employeeId) {
      const fetchCallLogs = async () => {
        try {
          const response = await axios.get(
            `http://localhost:3005/employees/${employeeId}/call-logs`
          );
          setCallLogs(response.data);
          setAllCallLogs(response.data); // Store all call logs
        } catch (err) {
          setError("Error fetching call logs");
          console.error("Error fetching call logs:", err.message || err);
        }
      };

      fetchCallLogs();
    } else {
      console.warn("employeeId is undefined. Cannot fetch call logs.");
    }
  }, [employeeId]);

  // Function to handle search
  const handleSearch = (e) => {
    const query = e.target.value; // Get the current input value
    setSearchQuery(query); // Update search query state

    // Filter call logs based on the search query
    if (query) {
      const filteredLogs = allCallLogs.filter((log) =>
        (log.name && log.name.toLowerCase().includes(query.toLowerCase())) ||
        (log.phoneNumber && log.phoneNumber.includes(query))
      );
      setCallLogs(filteredLogs); // Update call logs state with filtered logs
    } else {
      setCallLogs(allCallLogs); // Reset to all call logs when input is empty
    }
  };

  // Function to handle filter type
  const handleFilterChange = (e) => {
    setFilterType(e.target.value); // Update filter type on change
    // Filter call logs based on type selection
    const filteredLogs =
      e.target.value === "All"
        ? allCallLogs
        : allCallLogs.filter((log) => log.type === e.target.value);
    setCallLogs(filteredLogs); // Update call logs state with filtered logs
  };

  // Function to delete a single call log
  const handleDelete = async (employeeId) => {
    const confirmDelete = window.confirm(
      "Do you really want to delete this call log?"
    );
    if (confirmDelete) {
      try {
        await axios.delete(`http://localhost:3005/call-logs/${employeeId}`);
        setCallLogs(callLogs.filter((call) => call._employeeId !== employeeId)); // Update state after deletion
        setAllCallLogs(allCallLogs.filter((call) => call._employeeId !== id)); // Update allCallLogs state
      } catch (error) {
        console.error("Error deleting call log:", error);
      }
    }
  };

  // Function to delete all call logs
  const handleDeleteAll = async () => {
    const confirmDelete = window.confirm(
      "Do you really want to delete all call logs?"
    );
    if (confirmDelete) {
      try {
        await axios.delete("http://localhost:3005/DeleteAll-call-logs");
        setCallLogs([]); // Clear state after deletion
        setAllCallLogs([]); // Clear allCallLogs state
      } catch (error) {
        console.error("Error deleting all call logs:", error);
      }
    }
  };

  return (
    <div className="p-6 absolute top-20 right-10 left-[270px]">
      <h2 className="text-3xl font-extrabold mb-6 text-gray-800">
        Call Logs for Employee ID: {employeeId}
      </h2>

      <div className="flex justify-between items-center mb-4">
        <input
          type="text"
          placeholder="Search by Name or Phone Number..."
          value={searchQuery}
          onChange={handleSearch}
          className="border border-purple-500 focus:border-purple-700 focus:ring-2 focus:ring-purple-500 p-2 rounded-lg w-1/3 shadow-sm"
        />
        <select
          value={filterType}
          onChange={handleFilterChange}
          className="border border-purple-500 p-2 rounded-lg focus:outline-none focus:border-purple-700"
        >
          <option value="All">All</option>
          <option value="incoming">Incoming</option>
          <option value="outgoing">Outgoing</option>
        </select>
        <button
          onClick={handleDeleteAll}
          className="bg-gradient-to-r from-red-500 to-purple-700 hover:shadow-lg text-white px-6 py-2 rounded-lg"
        >
          Delete All Call Logs
        </button>
      </div>

      {callLogs.length > 0 ? (
        <div className="overflow-x-auto">
          <table className="table-auto w-full text-left border-collapse shadow-lg rounded-lg overflow-hidden">
            <thead>
              <tr className="bg-gradient-to-r from-purple-600 to-purple-400 text-white">
                <th className="p-4">Sl. No.</th>
                <th className="p-4">Name</th>
                <th className="p-4">Type</th>
                <th className="p-4">Duration (seconds)</th>
                <th className="p-4">Date & Time</th>
                <th className="p-4">Phone Number</th>
                <th className="p-4 text-center">Actions</th>
              </tr>
            </thead>
            <tbody className="text-gray-700">
              {callLogs.map((log, index) => (
                <tr
                  key={log.timestamp}
                  className="bg-white border-b border-purple-200 hover:bg-purple-50 transition duration-300"
                >
                  <td className="p-4">{index + 1}</td>
                  <td className="p-4">{log.name || "N/A"}</td>
                  <td className="p-4">{log.type}</td>
                  <td className="p-4">{log.duration}</td>
                  <td className="p-4">
                    {new Date(log.dateTime).toLocaleString()}
                  </td>
                  <td className="p-4">{log.phoneNumber}</td>
                  <td className="p-4 text-center">
                    <button
                      onClick={() => handleDelete(log._id)}
                      className="bg-gradient-to-r from-red-500 to-purple-700 hover:shadow-lg text-white px-4 py-2 rounded-lg"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <p className="text-center text-lg text-gray-600">
          No call logs available
        </p>
      )}
    </div>
  );
};

export default CallLogTable;
