import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Calendar from "react-calendar";
import 'react-calendar/dist/Calendar.css';

const AttendanceTable = () => {
  const [employee, setEmployee] = useState(null);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch employee data, including attendance, from the backend
    const fetchEmployeeAttendance = async () => {
      try {
        setLoading(true);
        const response = await fetch("/api/employee-attendance"); // API endpoint for fetching employee data
        const data = await response.json();
        setEmployee(data);  // Assuming data contains an array of attendance records
        setLoading(false);
      } catch (err) {
        setError("Failed to fetch employee attendance data");
        setLoading(false);
      }
    };

    fetchEmployeeAttendance();
  }, []);

  const getDaysInMonth = (date) => {
    const year = date.getFullYear();
    const month = date.getMonth();
    return new Array(31).fill(null).map((_, i) => {
      const d = new Date(year, month, i + 1);
      return d.getMonth() === month ? d.getDate() : null;
    }).filter(Boolean);
  };

  const daysInMonth = getDaysInMonth(selectedDate);

  if (loading) {
    return <div>Loading attendance data...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  if (!employee) {
    return <div>No employee data found</div>;
  }

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-4">Employee Attendance</h1>
      <div className="flex justify-between mb-6">
        <div>
          <p><strong>Employee ID:</strong> {employee.employeeId}</p>
          <p><strong>Name:</strong> {employee.name}</p>
          <p><strong>Phone Number:</strong> {employee.phoneNumber}</p>
          <p><strong>Selected Date:</strong> {selectedDate.toDateString()}</p>
        </div>
        <div>
          <Calendar value={selectedDate} onChange={setSelectedDate} />
        </div>
      </div>

      <h2 className="text-lg font-bold mb-2">Attendance for {selectedDate.toLocaleString('default', { month: 'long', year: 'numeric' })}</h2>
      
      <table className="min-w-full bg-white border border-gray-300">
        <thead>
          <tr>
            <th className="border border-gray-300 px-4 py-2">Date</th>
            <th className="border border-gray-300 px-4 py-2">Status</th>
            <th className="border border-gray-300 px-4 py-2">Reason for Leave (if any)</th>
          </tr>
        </thead>
        <tbody>
          {daysInMonth.map((day) => {
            const attendanceForDay = employee.attendance.find(att => new Date(att.date).getDate() === day);  // Find attendance for the day
            return (
              <tr key={day} className="hover:bg-gray-100 transition-colors duration-200">
                <td className="border border-gray-300 px-4 py-2">{day}</td>
                <td className="border border-gray-300 px-4 py-2">
                  <span className={`inline-block px-3 py-1 rounded-full text-sm font-bold ${
                    attendanceForDay?.isPresent ? "bg-green-100 text-green-600" : "bg-red-100 text-red-600"
                  }`}>
                    {attendanceForDay?.isPresent ? "Present" : "Absent"}
                  </span>
                </td>
                <td className="border border-gray-300 px-4 py-2">
                  {!attendanceForDay?.isPresent && (
                    <span className="text-red-500">{attendanceForDay?.leaveReason || "No reason provided"}</span>
                  )}
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>

      <div className="mt-6">
        <Link to="/leave-requests" className="text-blue-500 hover:underline mr-4">View Leave Requests</Link>
        <Link to="/complaints" className="text-blue-500 hover:underline">View Complaints</Link>
      </div>
    </div>
  );
};

export default EmployeeAttendance;
