import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const MessagesData = () => {
  const { employeeId } = useParams();
  const [messages, setMessages] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [filteredMessages, setFilteredMessages] = useState([]);

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await axios.get(
          `http://localhost:3005/employees/${employeeId}/messages`
        );
        setMessages(response.data);
        setFilteredMessages(response.data);
      } catch (error) {
        console.error("Error fetching messages:", error);
      }
    };
    fetchMessages();
  }, [employeeId]);

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleDelete = async (messageId) => {
    if (window.confirm("Do you really want to delete this message?")) {
      try {
        await axios.delete(
          `http://localhost:3005/Delete-message/${employeeId}/${messageId}`
        );
        setMessages((prevMessages) =>
          prevMessages.filter((msg) => msg._id !== messageId)
        );
        setFilteredMessages((prevFiltered) =>
          prevFiltered.filter((msg) => msg._id !== messageId)
        );
      } catch (error) {
        console.error("Error deleting message:", error);
      }
    }
  };

  const handleDeleteAll = async () => {
    if (window.confirm("Do you really want to delete all messages?")) {
      try {
        await axios.delete(`http://localhost:3005/DeleteAll-messages`);
        setMessages([]);
        setFilteredMessages([]);
      } catch (error) {
        console.error("Error deleting all messages:", error);
      }
    }
  };

  useEffect(() => {
    const filtered = messages.filter((msg) => {
      const address = msg.address || "";
      const body = msg.body || "";
      return (
        address.toLowerCase().includes(searchTerm.toLowerCase()) ||
        body.toLowerCase().includes(searchTerm.toLowerCase())
      );
    });
    setFilteredMessages(filtered);
  }, [searchTerm, messages]);

  return (
    <div className="p-6 absolute top-20 right-10 left-[270px]">
      <h2 className="text-3xl font-extrabold mb-6 text-gray-800">
        Messages for Employee ID: {employeeId}
      </h2>

      <div className="flex justify-between items-center mb-4">
        <input
          type="text"
          placeholder="Search by Address or Message Body..."
          value={searchTerm}
          onChange={handleSearchChange}
          className="border border-purple-500 focus:border-purple-700 focus:ring-2 focus:ring-purple-500 p-2 rounded-lg w-1/3 shadow-sm"
        />
        <button
          onClick={handleDeleteAll}
          className="bg-gradient-to-r from-red-500 to-purple-700 hover:shadow-lg text-white px-6 py-2 rounded-lg"
        >
          Delete All Messages
        </button>
      </div>

      {filteredMessages.length > 0 ? (
        <div className="overflow-x-auto absolute top-20 right-10 left-[270px]">
          <table className="table-auto w-full text-left border-collapse shadow-lg rounded-lg overflow-hidden">
            <thead>
              <tr className="bg-gradient-to-r from-purple-600 to-purple-400 text-white">
                <th className="p-4">Address</th>
                <th className="p-4">Message Body</th>
                <th className="p-4">Service Center</th>
                <th className="p-4">Received Date</th>
                <th className="p-4">Received Time</th>
                <th className="p-4 text-center">Actions</th>
              </tr>
            </thead>
            <tbody className="text-gray-700">
              {filteredMessages.map((msg, index) => (
                <tr
                  key={`${msg._id}-${index}`}
                  className="bg-white border-b border-purple-200 hover:bg-purple-50 transition duration-300"
                >
                  <td className="p-4">{msg.address}</td>
                  <td className="p-4">{msg.body}</td>
                  <td className="p-4">{msg.service_center}</td>
                  <td className="p-4">
                    {new Date(msg.date).toLocaleDateString()}
                  </td>
                  <td className="p-4">
                    {new Date(msg.date).toLocaleTimeString()}
                  </td>
                  <td className="p-4 text-center">
                    <button
                      onClick={() => handleDelete(msg._id)}
                      className="bg-gradient-to-r from-red-500 to-purple-700 hover:shadow-lg text-white px-4 py-2 rounded-lg"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <p className="text-center text-lg text-gray-600">
          No messages available
        </p>
      )}
    </div>
  );
};

export default MessagesData;
