import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import { BASE_URL } from './constants';

const AttendanceDetails = () => {
  const [employee, setEmployee] = useState(null);
  const [attendance, setAttendance] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [employeeId, setEmployeeId] = useState("");
  const [date, setDate] = useState(new Date());
  const navigate = useNavigate();

  const fetchEmployeeAttendance = async (id) => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(`${BASE_URL}/employees/${id}/attendance-list`);
      setEmployee(response.data);
      setAttendance(response.data.attendanceRecords || []);
    } catch (err) {
      setError("Error fetching employee data. Please try again later.");
      console.error("Error fetching employee data:", err);
    } finally {
      setLoading(false);
    }
  };

  const generateMonthDays = (year, month) => {
    const daysInMonth = new Date(year, month + 1, 0).getDate();
    return Array.from({ length: daysInMonth }, (_, i) => new Date(year, month, i + 1));
  };

  const fullMonthAttendance = generateMonthDays(date.getFullYear(), date.getMonth()).map((day) => {
    const record = attendance.find(
      (att) => new Date(att.location.time).toDateString() === day.toDateString()
    );
    let status = record
      ? record.AttendanceStatus.isPresent
        ? "Present"
        : record.AttendanceStatus.isHalfDay
        ? "Half Day"
        : record.AttendanceStatus.isLeave
        ? "Leave"
        : "Absent"
      : "Absent";

    return {
      date: day,
      status,
    };
  });

  const handleSearch = (e) => {
    e.preventDefault();
    if (employeeId.trim()) {
      fetchEmployeeAttendance(employeeId);
    }
  };

  return (
    <div className="p-4 max-w-lg mx-auto bg-gray-100 rounded-lg shadow-lg">
      <h2 className="text-3xl font-bold mb-6 text-center text-indigo-600">
        Attendance Tracker
      </h2>

      <form onSubmit={handleSearch} className="flex justify-center mb-4">
        <input
          type="text"
          placeholder="Employee ID"
          value={employeeId}
          onChange={(e) => setEmployeeId(e.target.value)}
          className="border border-gray-300 rounded-l-md px-4 py-2 w-full sm:w-64"
        />
        <button
          type="submit"
          className="bg-indigo-500 hover:bg-indigo-700 text-white font-bold py-2 px-4 rounded-r-md"
        >
          Search
        </button>
      </form>

      <div className="mb-6 text-center">
        <Calendar onChange={setDate} value={date} className="mx-auto" />
        <p className="text-gray-600 mt-2">
          {date.toLocaleString("default", { month: "long", year: "numeric" })}
        </p>
      </div>

      {loading ? (
        <p className="text-center text-gray-500">Loading...</p>
      ) : error ? (
        <p className="text-center text-red-500">{error}</p>
      ) : employee ? (
        <div>
          <div className="bg-indigo-500 text-white p-4 rounded-md mb-4">
            <h3 className="text-xl font-semibold">Employee Information</h3>
            <p>ID: {employee.employeeId}</p>
            <p>Name: {employee.name}</p>
          </div>

          <div className="bg-white rounded-lg shadow-md p-4">
            <h3 className="text-lg font-bold mb-3 text-indigo-600">
              Attendance Overview
            </h3>
            <ul className="space-y-2">
              {fullMonthAttendance.map((record, index) => (
                <li
                  key={index}
                  className="flex justify-between bg-gray-50 p-3 rounded-md shadow-sm"
                >
                  <span>{record.date.toLocaleDateString()}</span>
                  <span
                    className={`font-semibold ${
                      record.status === "Present"
                        ? "text-green-600"
                        : record.status === "Half Day"
                        ? "text-yellow-500"
                        : record.status === "Leave"
                        ? "text-blue-500"
                        : "text-red-500"
                    }`}
                  >
                    {record.status}
                  </span>
                </li>
              ))}
            </ul>
          </div>
        </div>
      ) : null}
    </div>
  );
};

export default AttendanceDetails;
