import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, Link } from "react-router-dom";  // Added Link for navigation
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

const AttendanceTable = () => {
  const [attendance, setAttendance] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { employeeId } = useParams();
  const [date, setDate] = useState(new Date());

  useEffect(() => {
    const fetchEmployeeAttendance = async () => {
      setLoading(true);
      try {
        const response = await axios.get(`http://localhost:3005/employees/${employeeId}/attendance-list`);
        const data = Array.isArray(response.data) ? response.data : [];
        console.log(data);  // Inspect the data structure
        setAttendance(data);
      } catch (err) {
        setError("Error fetching employee data. Please try again later.");
        console.error("Error fetching employee data:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchEmployeeAttendance();
  }, [employeeId]);

  const generateMonthDays = (year, month) => {
    const daysInMonth = new Date(year, month + 1, 0).getDate();
    const days = [];
    for (let day = 1; day <= daysInMonth; day++) {
      days.push(new Date(year, month, day));
    }
    return days;
  };

  const fullMonthAttendance = generateMonthDays(date.getFullYear(), date.getMonth()).map(day => {
    const record = (attendance || []).find(att => new Date(att.time).toDateString() === day.toDateString());
    let status = "Absent";
    if (record) {
      if (record.AttendanceStatus.isPresent) {
        status = "Present";
      } else if (record.AttendanceStatus.isHalfDay) {
        status = "Half Day";
      } else if (record.AttendanceStatus.isLeave) {
        status = "Leave";
      }
    }

    return {
      date: day,
      status,
    };
  });

  return (
    <div className="p-6 max-w-7xl mx-auto bg-gray-100 rounded-lg shadow-lg absolute top-20 right-10 left-[275px]">
      <h2 className="text-4xl font-bold mb-6 text-center text-blue-600">Employee Attendance</h2>

      {/* Add buttons for Leave Request and Complaints at the top */}
      <div className="flex justify-center space-x-4 mb-6">
        {/* Leave Request Button */}
        <Link to={`/employees/${employeeId}/leaves`}>
          <button className="px-6 py-2 bg-blue-500 text-white rounded-lg shadow hover:bg-blue-600 transition duration-300">
            View Leave Requests
          </button>
        </Link>
        
        {/* Complaint Button */}
        <Link to={`/employees/${employeeId}/complaints`}>
          <button className="px-6 py-2 bg-red-500 text-white rounded-lg shadow hover:bg-red-600 transition duration-300">
            View Complaints
          </button>
        </Link>
      </div>

      <div className="mb-6">
        <Calendar 
          onChange={setDate} 
          value={date} 
          className="mx-auto" 
        />
        <p className="text-center text-gray-600 mt-2">Selected Month: {date.toLocaleString('default', { month: 'long', year: 'numeric' })}</p>
      </div>

      {loading ? (
        <p className="text-center text-gray-500">Loading attendance...</p>
      ) : error ? (
        <p className="text-center text-red-500">{error}</p>
      ) : (
        <div className="mb-8">
          <table className="min-w-full bg-white mt-4 border border-gray-300 rounded-lg shadow-md">
            <thead className="bg-gray-200">
              <tr>
                <th className="border border-gray-300 px-4 py-2 text-left text-sm font-medium text-gray-600">Date</th>
                <th className="border border-gray-300 px-4 py-2 text-left text-sm font-medium text-gray-600">Status</th>
              </tr>
            </thead>
            <tbody>
              {fullMonthAttendance.map(({ date, status }) => (
                <tr key={date.toDateString()} className="hover:bg-gray-100 transition-colors duration-200">
                  <td className="border border-gray-300 px-4 py-2">
                    {date.toDateString()}
                  </td>
                  <td className="border border-gray-300 px-4 py-2">
                    <span className={`inline-block px-3 py-1 rounded-full text-sm font-bold ${
                      status === "Present" ? "bg-green-100 text-green-600" :
                      status === "Half Day" ? "bg-yellow-100 text-yellow-600" :
                      status === "Leave" ? "bg-blue-100 text-blue-600" :
                      "bg-red-100 text-red-600"
                    }`}>
                      {status}
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default AttendanceTable;
