import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

const MessagesData = () => {
  const { employeeId } = useParams();
  const [messages, setMessages] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [filteredMessages, setFilteredMessages] = useState([]);
  const navigate = useNavigate();

  // Fetch messages from backend API
  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await axios.get(`http://localhost:3005/employees/${employeeId}/messages`);
        setMessages(response.data);
        setFilteredMessages(response.data);
      } catch (error) {
        console.error("Error fetching messages:", error);
      }
    };
    fetchMessages();
  }, [employeeId]);

  // Handle search input change
  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  // Handle delete a single message with confirmation
  const handleDelete = async (messageId) => {
    if (window.confirm("Do you really want to delete this message?")) {
      try {
        await axios.delete(`http://localhost:3005/Delete-message/${employeeId}/${messageId}`);
        // Update local state to reflect the deleted message
        setMessages((prevMessages) => prevMessages.filter((msg) => msg._id !== messageId));
        setFilteredMessages((prevFiltered) => prevFiltered.filter((msg) => msg._id !== messageId));
      } catch (error) {
        console.error("Error deleting message:", error);
      }
    }
  };

  // Handle delete all messages with confirmation
  const handleDeleteAll = async () => {
    if (window.confirm("Do you really want to delete all messages?")) {
      try {
        await axios.delete("http://localhost:3005/DeleteAll-messages");
        setMessages([]);
        setFilteredMessages([]);
      } catch (error) {
        console.error("Error deleting all messages:", error);
      }
    }
  };

  // Filter messages based on search term
  useEffect(() => {
    const filtered = messages.filter((msg) => {
      const address = msg.address || "";
      const body = msg.body || "";
      return (
        address.toLowerCase().includes(searchTerm.toLowerCase()) ||
        body.toLowerCase().includes(searchTerm.toLowerCase())
      );
    });
    setFilteredMessages(filtered);
  }, [searchTerm, messages]);

  return (
    <div className="p-4">
      <input
        type="text"
        placeholder="Search messages..."
        value={searchTerm}
        onChange={handleSearchChange}
        className="border p-2 rounded mb-4"
      />
      <button
        onClick={handleDeleteAll}
        className="ml-4 bg-gradient-to-tr from-red-500 hover:shadow-lg to-black text-white px-4 py-2 rounded mb-4"
      >
        Delete All Messages
      </button>
      <h2 className="text-xl font-bold mb-4">Messages for Employee ID: {employeeId}</h2>
      {filteredMessages.length > 0 ? (
        <table className="min-w-full bg-white border border-gray-300 rounded-lg shadow-md">
          <thead className="bg-gray-100">
            <tr>
              <th className="border border-gray-300 px-6 py-3 text-left text-sm font-medium text-gray-600">Address</th>
              <th className="border border-gray-300 px-6 py-3 text-left text-sm font-medium text-gray-600">Message Body</th>
              <th className="border border-gray-300 px-6 py-3 text-left text-sm font-medium text-gray-600">Service Center</th>
              <th className="border border-gray-300 px-6 py-3 text-left text-sm font-medium text-gray-600">Received Date</th>
              <th className="border border-gray-300 px-6 py-3 text-left text-sm font-medium text-gray-600">Received Time</th>
              <th className="border border-gray-300 px-6 py-3 text-left text-sm font-medium text-gray-600">Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredMessages.map((msg) => (
              <tr key={msg._id} className="hover:bg-gray-100 transition-colors duration-200">
                <td className="border border-gray-300 px-6 py-4">{msg.address}</td>
                <td className="border border-gray-300 px-6 py-4">{msg.body}</td>
                <td className="border border-gray-300 px-6 py-4">{msg.service_center}</td>
                <td className="border border-gray-300 px-6 py-4">{new Date(msg.date).toLocaleDateString()}</td>
                <td className="border border-gray-300 px-6 py-4">{new Date(msg.date).toLocaleTimeString()}</td>
                <td className="border border-gray-300 px-6 py-4">
                  <button
                    onClick={() => handleDelete(msg._id)}
                    className="bg-gradient-to-tr from-red-500 hover:shadow-lg to-black text-white px-4 py-2 rounded"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No messages available</p>
      )}
    </div>
  );
};

export default MessagesData;
