import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

const CallLogTable = () => {
  const { employeeId } = useParams(); // Get employeeId from URL
  const [callLogs, setCallLogs] = useState([]);
  const [allCallLogs, setAllCallLogs] = useState([]); // Store all call logs
  const [searchQuery, setSearchQuery] = useState("");
  const [filterType, setFilterType] = useState("All"); // For filtering call type
  const [error, setError] = useState("");
  const navigate = useNavigate(); // Initialize navigate

  // Fetch call logs whenever employeeId changes
  useEffect(() => {
    if (employeeId) {
      const fetchCallLogs = async () => {
        try {
          const response = await axios.get(`http://localhost:3005/employees/${employeeId}/call-logs`);
          setCallLogs(response.data);
          setAllCallLogs(response.data); // Store all call logs
        } catch (err) {
          setError("Error fetching call logs");
          console.error("Error fetching call logs:", err.message || err);
        }
      };

      fetchCallLogs();
    } else {
      console.warn("employeeId is undefined. Cannot fetch call logs.");
    }
  }, [employeeId]);

  // Function to handle search
  const handleSearch = (e) => {
    const query = e.target.value; // Get the current input value
    setSearchQuery(query); // Update search query state

    // Filter call logs based on the search query
    if (query) {
      const filteredLogs = allCallLogs.filter(log =>
        (log.name && log.name.toLowerCase().includes(query.toLowerCase())) ||
        (log.phoneNumber && log.phoneNumber.includes(query))
      );
      setCallLogs(filteredLogs); // Update call logs state with filtered logs
    } else {
      setCallLogs(allCallLogs); // Reset to all call logs when input is empty
    }
  };

  // Function to handle filter type
  const handleFilterChange = (e) => {
    setFilterType(e.target.value); // Update filter type on change
    // Filter call logs based on type selection
    const filteredLogs = e.target.value === "All" ? allCallLogs : allCallLogs.filter(log => log.type === e.target.value);
    setCallLogs(filteredLogs); // Update call logs state with filtered logs
  };

  // Function to delete a single call log
  const handleDelete = async (id) => {
    const confirmDelete = window.confirm("Do you really want to delete this call log?");
    if (confirmDelete) {
      try {
        await axios.delete(`http://localhost:3005/call-logs/${id}`);
        setCallLogs(callLogs.filter((call) => call._id !== id)); // Update state after deletion
        setAllCallLogs(allCallLogs.filter((call) => call._id !== id)); // Update allCallLogs state
      } catch (error) {
        console.error("Error deleting call log:", error);
      }
    }
  };

  // Function to delete all call logs
  const handleDeleteAll = async () => {
    const confirmDelete = window.confirm("Do you really want to delete all call logs?");
    if (confirmDelete) {
      try {
        await axios.delete("http://localhost:3005/DeleteAll-call-logs");
        setCallLogs([]); // Clear state after deletion
        setAllCallLogs([]); // Clear allCallLogs state
      } catch (error) {
        console.error("Error deleting all call logs:", error);
      }
    }
  };

  // Function to navigate to a specific call log details page
  const navigateToDetails = (logId) => {
    navigate(`/call-logs/${logId}`); // Navigates to the call log details page
  };

  return (
    <div className="overflow-x-auto absolute top-20 left-[270px] right-10">
      <div className="flex justify-between items-center">
        <input
          type="text"
          placeholder="Search by Name or Phone Number..."
          className="transition rounded-sm mt-4 border border-blue-700 p-3 w-60"
          value={searchQuery}
          onChange={handleSearch} // Update search query on change
        />
        <select
          value={filterType}
          onChange={handleFilterChange} // Update filter type on change
          className="mt-4 border border-blue-700 p-2 rounded-sm"
        >
          <option value="All">All</option>
          <option value="incoming">Incoming</option>
          <option value="outgoing">Outgoing</option>
        </select>
        <button
          onClick={handleDeleteAll}
          className="bg-gradient-to-tr from-red-500 hover:shadow-lg to-black text-white p-2 rounded mt-4"
        >
          Delete All Call Logs
        </button>
      </div>

      <div className="container mx-auto p-4">
        <h2 className="text-xl font-bold mb-4">Call Logs for Employee ID: {employeeId}</h2>
        {error && <p className="text-red-500">{error}</p>}

        {callLogs.length > 0 ? (
          <table className="min-w-full bg-white rounded-lg shadow-md mt-4">
            <thead>
              <tr className="bg-gray-200 text-gray-600 uppercase text-sm leading-normal">
                <th className="py-3 px-6 text-left">Sl. No.</th>
                <th className="py-3 px-6 text-left">Name</th>
                <th className="py-3 px-6 text-left">Type</th>
                <th className="py-3 px-6 text-left">Duration (seconds)</th>
                <th className="py-3 px-6 text-left">Date & Time</th>
                <th className="py-3 px-6 text-left">Phone Number</th>
                <th className="py-3 px-6 text-left">Actions</th>
              </tr>
            </thead>
            <tbody className="text-gray-600 text-sm font-light">
              {callLogs.map((log, index) => (
                <tr
                  key={log.timestamp}
                  className="border-b border-gray-200 hover:bg-gray-100"
                >
                  <td className="py-3 px-6 text-left">{index + 1}</td>
                  <td className="py-3 px-6 text-left">{log.name}</td>
                  <td className="py-3 px-6 text-left">{log.type}</td>
                  <td className="py-3 px-6 text-left">{log.duration}</td>
                  <td className="py-3 px-6 text-left">
                    {new Date(log.dateTime).toLocaleString()}
                  </td>
                  <td className="py-3 px-6 text-left">{log.phoneNumber}</td>
                  <td className="py-3 px-6 text-left">
                    <button
                      className="bg-red-500 text-white p-1 rounded mr-2"
                      onClick={() => handleDelete(log._id)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p className="text-center mt-4">No call logs available.</p>
        )}
      </div>
    </div>
  );
};

export default CallLogTable;
