import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserRequest = () => {
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchEmployees();
  }, []);

  const fetchEmployees = async () => {
    try {
      const response = await axios.get("http://localhost:3005/employees");
      setEmployees(response.data);
    } catch (err) {
      setError("Error fetching employee data.");
    } finally {
      setLoading(false);
    }
  };

  const handleApprove = (id) => {
    // Implement the approve functionality here
    console.log("Approved employee with ID:", id);
  };

  const handleReject = (id) => {
    // Implement the reject functionality here
    console.log("Rejected employee with ID:", id);
  };

  const viewDetails = (id) => {
    // Implement view functionality (e.g., show a modal with employee details)
    console.log("Viewing details for employee with ID:", id);
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="employee-dashboard">
      <h1>Employee Dashboard</h1>
      <table>
        <thead>
          <tr>
            <th>Employee ID</th>
            <th>Name</th>
            <th>Number</th>
            <th>Details</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {employees.map((employee) => (
            <tr key={employee.employeeId}>
              <td>{employee.employeeId}</td>
              <td>{employee.name}</td>
              <td>{employee.number}</td>
              <td>
                <button onClick={() => viewDetails(employee.employeeId)}>
                  View
                </button>
              </td>
              <td>
                <button onClick={() => handleApprove(employee.employeeId)}>
                  Approve
                </button>
                <button onClick={() => handleReject(employee.employeeId)}>
                  Reject
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UserRequest;
