import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const LeaveForm = () => {
  const { employeeId, name, mobileNumber } = useParams(); // Get the employeeId from the URL
  const [leaveRequests, setLeaveRequests] = useState([]);
  const [employee, setEmployee] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch employee and leave requests data
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        // Fetch employee details based on the provided employeeId
        const employeeResponse = await axios.get(`http://localhost:3005/employees/${employeeId}/attendance-list`);
        setEmployee(employeeResponse.data);

        // Fetch leave requests for the specific employee
        const leaveRequestsResponse = await axios.get(`http://localhost:3005/employees/${employeeId}/attendance-list/leaves`);
        setLeaveRequests(leaveRequestsResponse.data);
      } catch (err) {
        setError("Error fetching data. Please try again later.");
        console.error("Error fetching data:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [employeeId]);

  // Sort leave requests by createdAt date in descending order
  const sortedLeaveRequests = leaveRequests.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));

  return (
    <div className="p-6  bg-gradient-to-b min-h-screen absolute top-20 right-10 left-[275px]">
      {/* Employee Information */}
      <div className="bg-gradient-to-r from-green-400 to-blue-400 p-4 rounded-lg shadow-lg mb-6">
        <h2 className="text-2xl font-bold text-white">Employee Information</h2>
        <p className="text-lg text-white">ID: {employee.employeeId || employeeId}</p>
        <p className="text-lg text-white">Name: {employee.name || name}</p>
        <p className="text-lg text-white">Number: {employee.mobileNumber || mobileNumber}</p>
      </div>

      {/* Leave Requests Section */}
      <h2 className="text-3xl font-bold text-black text-center mb-6">
        Leave Requests Inbox
      </h2>

      {loading ? (
        <p className="text-center text-white">Loading...</p>
      ) : error ? (
        <p className="text-center text-red-60 ">{error}</p>
      ) : sortedLeaveRequests.length > 0 ? (
        <div className="space-y-4">
          {sortedLeaveRequests.map((request, index) => (
            <div
              key={index}
              className="bg-white p-4 rounded-lg shadow-lg border-l-4 border-blue-500"
            >
              <h3 className="text-xl font-semibold text-blue-600">
                <span className="text-red-500">Leave Type: {request.leaveType}</span><br />
                <span>Reason: {request.reason}</span>
              </h3>
              
              <p className="text-gray-700">
  Start Date: {new Date(request.startDate).toLocaleDateString()}
</p>
<p className="text-gray-700">
  End Date: {new Date(request.endDate).toLocaleDateString()}
</p>

              <p className="text-gray-700 flex absolute top-0">Applied Date: {request.createdAt}</p>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-center text-black">No leave requests found.</p>
      )}
    </div>
  );
};

export default LeaveForm;
