import React, { useEffect, useState } from "react";
import axios from "axios";

const CallLogDetails = () => {
  const [employeeId, setEmployeeId] = useState("");
  const [callLogs, setCallLogs] = useState([]);
  const [filteredCallLogs, setFilteredCallLogs] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [sortOrder, setSortOrder] = useState("");
  const [filterType, setFilterType] = useState("All");
  const [errorMessage, setErrorMessage] = useState("");

  const handleEmployeeIdChange = (event) => {
    setEmployeeId(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!employeeId) {
      setErrorMessage("Please enter an Employee ID.");
      return;
    }
    try {
      const response = await axios.get(`http://localhost:3005/employees/${employeeId}/call-logs`);
      if (response.data.length === 0) {
        setErrorMessage("No call logs found for this Employee ID.");
        setCallLogs([]);
        setFilteredCallLogs([]);
      } else {
        setCallLogs(response.data);
        setFilteredCallLogs(response.data);
        setErrorMessage(""); // Clear error message if call logs found
      }
    } catch (error) {
      console.error("Error fetching call logs:", error);
      setErrorMessage("Invalid Employee ID. Please try again.");
      setCallLogs([]);
      setFilteredCallLogs([]);
    }
  };

  const handleSort = (order) => {
    setSortOrder(order);
    const sortedLogs = [...filteredCallLogs].sort((a, b) => {
      if (order === "ascending") {
        return a.duration - b.duration;
      } else if (order === "descending") {
        return b.duration - a.duration;
      }
      return 0;
    });
    setFilteredCallLogs(sortedLogs);
  };

  const handleDeleteCallLog = async (callLogId) => {
    if (window.confirm("Do you really want to delete this call log?")) {
      try {
        await axios.delete(
          `http://localhost:3005/Delete-call-log/${employeeId}/${callLogId}`
        );
        setCallLogs((prevLogs) => prevLogs.filter((log) => log._id !== callLogId));
        setFilteredCallLogs((prevFiltered) =>
          prevFiltered.filter((log) => log._id !== callLogId)
        );
      } catch (error) {
        console.error("Error deleting call log:", error);
      }
    }
  };

  const handleDeleteAllCallLogs = async () => {
    if (window.confirm("Do you really want to delete all call logs? This action cannot be undone.")) {
      try {
        await axios.delete(`http://localhost:3005/Delete-all-call-logs/${employeeId}`);

        // Clear the call logs in the state
        setCallLogs([]); // Empty the call logs state
        setFilteredCallLogs([]); // Empty the filtered call logs state
        alert("All call logs deleted successfully.");
      } catch (error) {
        console.error("Error deleting all call logs:", error);
        alert("Error deleting all call logs. Please try again.");
      }
    }
  };

  // ... (rest of your existing code)

  return (
    <div className="p-6 absolute top-20 right-10 left-[270px]">
      <h2 className="text-3xl font-extrabold mb-6 text-gray-800">Call Logs</h2>

      {/* Employee ID Input Section */}
      <form onSubmit={handleSubmit} className="mb-4">
        <input
          type="text"
          placeholder="Enter Employee ID"
          value={employeeId}
          onChange={handleEmployeeIdChange}
          className="border border-purple-500 focus:border-purple-700 focus:ring-2 focus:ring-purple-500 p-2 rounded-lg w-1/3 shadow-sm"
        />
        <button type="submit" className="ml-4 bg-gradient-to-r from-blue-500 to-blue-700 hover:shadow-lg text-white px-6 py-2 rounded-lg">
          Submit
        </button>
      </form>

      {/* Error Message */}
      {errorMessage && <p className="text-red-500">{errorMessage}</p>}

      {/* Call Logs Table */}
      {filteredCallLogs.length > 0 ? (
        <div className="overflow-x-auto">
          <table className="table-auto w-full text-left border-collapse shadow-lg rounded-lg overflow-hidden">
            <thead>
              <tr className="bg-gradient-to-r from-purple-600 to-purple-400 text-white text-xl">
                <th className="p-4">Sl. No.</th>
                <th className="p-4">Name</th>
                <th className="p-4">Type</th>
                <th className="p-4">Duration (minutes)</th>
                <th className="p-4">Date & Time</th>
                <th className="p-4">Phone Number</th>
                <th className="p-4 text-center">Actions</th>
              </tr>
            </thead>
            <tbody className="text-gray-700">
              {filteredCallLogs.map((log, index) => (
                <tr key={log._id} className="bg-white border-b border-purple-200 hover:bg-purple-50 transition duration-300">
                  <td className="p-4">{index + 1}</td>
                  <td className="p-4">{log.name || "N/A"}</td>
                  <td className="p-4">{log.type}</td>
                  <td className="p-4">{(log.duration / 60).toFixed(2)}</td>
                  <td className="p-4">{new Date(log.dateTime).toLocaleString()}</td>
                  <td className="p-4">{log.phoneNumber}</td>
                  <td className="p-4 text-center">
                    <button
                      onClick={() => handleDeleteCallLog(log._id)}
                      className="bg-gradient-to-r from-red-500 to-purple-700 hover:shadow-lg text-white px-4 py-2 rounded-lg"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        !errorMessage && <p className="text-center text-lg text-gray-600">No call logs available</p>
      )}
    </div>
  );
};

export default CallLogDetails;
