import React, { useEffect, useState } from 'react';
import axios from 'axios';

const MessagesCard = () => {
  const [messages, setMessages] = useState([]);
  const [filteredMessages, setFilteredMessages] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await axios.get('http://localhost:3005/getmessages');
        setMessages(response.data);
        setFilteredMessages(response.data); // Initialize filtered messages
      } catch (error) {
        console.error('Error fetching messages:', error);
      }
    };

    fetchMessages();
  }, []);

  const handleSearch = async (e) => {
    const query = e.target.value;
    setSearchQuery(query);

    if (query.trim() === '') {
      setFilteredMessages(messages); // Reset to all messages if query is empty
    } else {
      try {
        const response = await axios.get(`http://localhost:3005/search-messages?query=${query}`);
        setFilteredMessages(response.data); // Update filtered messages with the search result
      } catch (error) {
        console.error('Error searching messages:', error);
        setFilteredMessages([]); // Clear messages on error
      }
    }
  };

  const handleDeleteMessage = async (id) => {
    try {
      await axios.delete(`http://localhost:3005/delete-message/${id}`);
      setFilteredMessages(filteredMessages.filter(msg => msg._id !== id)); // Update state after deletion
    } catch (error) {
      console.error('Error deleting message:', error);
    }
  };

  const handleDeleteAllMessages = async () => {
    if (window.confirm('Do you really want to delete all messages?')) {
      try {
        await axios.delete('http://localhost:3005/delete-all-messages');
        setFilteredMessages([]); // Clear the filtered messages
        setMessages([]); // Clear the original messages as well
      } catch (error) {
        console.error('Error deleting all messages:', error);
      }
    }
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Messages</h2>
      <input
        type="text"
        placeholder="Search messages..."
        value={searchQuery}
        onChange={handleSearch}
        className="mb-4 p-2 border rounded"
      />
      <button onClick={handleDeleteAllMessages} className="mb-4 bg-red-500 text-white p-2 rounded">
        Delete All Messages
      </button>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {filteredMessages.length > 0 ? (
          filteredMessages.map((msg) => (
            <div key={msg._id} className="bg-white p-4 rounded-lg shadow-md flex flex-col justify-between">
              <div className="mb-4">
                <p className="text-gray-600 font-semibold">From: {msg.phoneNumber}</p>
                <p className="text-gray-800 mt-2">{msg.messageContent}</p>
              </div>
              <div className="text-gray-500 text-sm">
                <p>Received: {new Date(msg.timestamp).toLocaleDateString()}</p>
                <p>Time: {new Date(msg.timestamp).toLocaleTimeString()}</p>
              </div>
              <button onClick={() => handleDeleteMessage(msg._id)} className="mt-2 bg-red-500 text-white p-1 rounded">
                Delete
              </button>
            </div>
          ))
        ) : (
          <p>No messages available</p>
        )}
      </div>
    </div>
  );
};

export default MessagesCard;
